import React from 'react';
import { motion } from 'framer-motion';

const HomePage = () => {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      style={{
        minHeight: 'calc(100vh - 70px)',
        background: 'linear-gradient(45deg, #000000 0%, #1a0000 100%)',
        padding: '50px 20px',
        textAlign: 'center'
      }}
    >
      <motion.h1 
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.3 }}
        style={{ 
          fontSize: '48px', 
          color: '#ff0000',
          textShadow: '0 0 10px #ff0000',
          marginBottom: '20px'
        }}
      >
        üî• –¢–ï–•–ù–ò–ß–ï–°–ö–ò–ô –°–ü–ï–¶–ò–ê–õ–ò–°–¢
      </motion.h1>
      
      <motion.p
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.5 }}
        style={{ 
          fontSize: '24px', 
          color: '#ffffff',
          maxWidth: '800px',
          margin: '0 auto 40px',
          lineHeight: '1.6'
        }}
      >
        <span style={{ color: '#ff0000' }}>–¢–µ—Ö-–ª–∏–¥ / Digital-—Ç–µ—Ö–Ω–æ–ª–æ–≥</span> <br/>
        –ê–≤—Ç–æ–≤–µ–±–∏–Ω–∞—Ä—ã ‚Ä¢ –ß–∞—Ç-–±–æ—Ç—ã ‚Ä¢ –ö–æ–º–ø–ª–µ–∫—Å–Ω—ã–µ –∑–∞–ø—É—Å–∫–∏
      </motion.p>
      
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        gap: '20px',
        flexWrap: 'wrap'
      }}>
        {[
          { icon: 'ü§ñ', text: '–ß–∞—Ç-–±–æ—Ç—ã', desc: 'Telegram, WhatsApp' },
          { icon: 'üé•', text: '–ê–≤—Ç–æ–≤–µ–±–∏–Ω–∞—Ä—ã', desc: '–ü—Ä–æ–¥–∞–∂–∏ 24/7' },
          { icon: 'üöÄ', text: '–ó–∞–ø—É—Å–∫–∏', desc: '–û–Ω–ª–∞–π–Ω-–∫—É—Ä—Å—ã, –º–∞—Ä–∞—Ñ–æ–Ω—ã' },
          { icon: 'üíª', text: '–¢–µ—Ö–ø–æ–¥–¥–µ—Ä–∂–∫–∞', desc: '–ê–±–æ–Ω–µ–Ω—Ç—Å–∫–æ–µ –æ–±—Å–ª—É–∂–∏–≤–∞–Ω–∏–µ' }
        ].map((item, index) => (
          <motion.div
            key={index}
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.7 + index * 0.1 }}
            whileHover={{ scale: 1.1, boxShadow: '0 0 20px #ff0000' }}
            whileTap={{ scale: 0.95 }}
            style={{
              background: 'rgba(255, 0, 0, 0.1)',
              border: '1px solid #ff0000',
              padding: '20px',
              borderRadius: '15px',
              color: 'white',
              width: '200px',
              cursor: 'pointer',
              transition: 'all 0.3s'
            }}
          >
            <div style={{ fontSize: '40px', marginBottom: '10px' }}>{item.icon}</div>
            <h3 style={{ margin: '10px 0', color: '#ff0000' }}>{item.text}</h3>
            <p style={{ fontSize: '14px', color: '#aaa' }}>{item.desc}</p>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default HomePage;
