import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import HomePage from './pages/HomePage';
import PricelistPage from './pages/PricelistPage';
import GamePage from './pages/GamePage';
import ContactsPage from './pages/ContactsPage';
import TelegramFloat from './components/TelegramFloat';
import { initTelegramWebApp } from './utils/telegramInit';

// Обертка для навигации с Telegram кнопкой "Назад"
function TelegramWrapper({ children }) {
  const [telegramData, setTelegramData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const tgData = initTelegramWebApp();
    setTelegramData(tgData);

    // Синхронизируем навигацию с кнопкой "Назад"
    if (tgData.isTelegram && tgData.webApp) {
      tgData.webApp.BackButton.onClick(() => {
        if (window.history.length > 1) {
          navigate(-1);
        } else {
          tgData.webApp.close();
        }
      });
    }

    // Применяем тему Telegram
    if (tgData.themeParams) {
      document.documentElement.style.setProperty('--tg-bg-color', tgData.themeParams.bg_color || '#000000');
      document.documentElement.style.setProperty('--tg-text-color', tgData.themeParams.text_color || '#ffffff');
      document.documentElement.style.setProperty('--tg-hint-color', tgData.themeParams.hint_color || '#aaaaaa');
      document.documentElement.style.setProperty('--tg-button-color', tgData.themeParams.button_color || '#f30808');
    }
  }, [navigate]);

  return children;
}

function App() {
  return (
    <Router>
      <TelegramWrapper>
        <div style={{
          minHeight: '100vh',
          background: 'var(--tg-bg-color, #000000)',
          color: 'var(--tg-text-color, #ffffff)',
          fontFamily: 'Arial, sans-serif',
          position: 'relative',
          transition: 'background 0.3s, color 0.3s'
        }}>
          <Navbar />
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/pricelist" element={<PricelistPage />} />
            <Route path="/game" element={<GamePage />} />
            <Route path="/contacts" element={<ContactsPage />} />
          </Routes>
          <TelegramFloat />
        </div>
      </TelegramWrapper>
    </Router>
  );
}

export default App;
