import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Confetti from 'react-confetti';

const GamePage = () => {
  // –°–æ—Å—Ç–æ—è–Ω–∏—è –∏–≥—Ä—ã
  const [level, setLevel] = useState(1);
  const [score, setScore] = useState(0);
  const [foundBugs, setFoundBugs] = useState([]);
  const [timeLeft, setTimeLeft] = useState(60);
  const [gameStatus, setGameStatus] = useState('playing'); // playing, win, lose
  const [showConfetti, setShowConfetti] = useState(false);
  const [promoCodes, setPromoCodes] = useState([]);
  const [hintsUsed, setHintsUsed] = useState(0);
  const [combo, setCombo] = useState(0);
  const [lastClickTime, setLastClickTime] = useState(0);
  const [difficulty, setDifficulty] = useState('normal'); // easy, normal, hard, expert
  const [specialBugs, setSpecialBugs] = useState([]);
  const [achievements, setAchievements] = useState([]);
  
  const gameAreaRef = useRef(null);
  const [gameAreaSize, setGameAreaSize] = useState({ width: 800, height: 500 });

  // –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–ª–æ–∂–Ω–æ—Å—Ç–∏
  const difficultySettings = {
    easy: { bugs: 3, time: 90, bugSize: 50 },
    normal: { bugs: 5, time: 60, bugSize: 40 },
    hard: { bugs: 7, time: 45, bugSize: 30 },
    expert: { bugs: 10, time: 30, bugSize: 20 }
  };

  // –¢–∏–ø—ã –±–∞–≥–æ–≤ —Å —Ä–∞–∑–Ω—ã–º–∏ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∞–º–∏
  const bugTypes = [
    { id: 1, name: '–°–ª–æ–º–∞–Ω–Ω—ã–π —á–∏–ø', emoji: 'üíæ', color: '#ff0000', points: 100, rare: false },
    { id: 2, name: '–ü–µ—Ä–µ–≥–æ—Ä–µ–≤—à–∏–π —Å–≤–µ—Ç–æ–¥–∏–æ–¥', emoji: 'üí°', color: '#ffff00', points: 150, rare: false },
    { id: 3, name: '–û—à–∏–±–∫–∞ –≤ –∫–æ–¥–µ', emoji: 'üêõ', color: '#00ff00', points: 200, rare: false },
    { id: 4, name: '–û–±—Ä—ã–≤ –ø—Ä–æ–≤–æ–¥–∞', emoji: 'üîå', color: '#ff8800', points: 120, rare: false },
    { id: 5, name: '–ü–µ—Ä–µ–≥—Ä–µ–≤ –ø—Ä–æ—Ü–µ—Å—Å–æ—Ä–∞', emoji: 'üî•', color: '#ff3300', points: 180, rare: false },
    { id: 6, name: '–í–∏—Ä—É—Å', emoji: 'ü¶†', color: '#ff00ff', points: 300, rare: true },
    { id: 7, name: '–£—Ç–µ—á–∫–∞ –ø–∞–º—è—Ç–∏', emoji: 'üíß', color: '#00ffff', points: 250, rare: true },
    { id: 8, name: '–ë–∏—Ç—ã–π –ø–∏–∫—Å–µ–ª—å', emoji: 'üß©', color: '#aa00ff', points: 220, rare: true }
  ];

  // –î–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω—ã–µ –ø—Ä–æ–º–æ–∫–æ–¥—ã —Å–æ —Å–∫–∏–¥–∫–∞–º–∏
  const validPromoCodes = [
    { code: 'TECH10', discount: 10, used: false },
    { code: 'LYOKORPS15', discount: 15, used: false },
    { code: 'MASTER20', discount: 20, used: false },
    { code: 'BUGHUNTER25', discount: 25, used: false }
  ];

  // –î–æ—Å—Ç–∏–∂–µ–Ω–∏—è
  const allAchievements = [
    { id: 1, name: '–ù–æ–≤–∏—á–æ–∫', desc: '–ù–∞–π—Ç–∏ –ø–µ—Ä–≤—ã–π –±–∞–≥', icon: 'üéØ', unlocked: false },
    { id: 2, name: '–ö–æ–º–±–∏–Ω–∞—Ç–æ—Ä', desc: '–°–æ–±—Ä–∞—Ç—å –∫–æ–º–±–æ x5', icon: '‚ö°', unlocked: false },
    { id: 3, name: '–°–∫–æ—Ä–æ—Å—Ç—Ä–µ–ª', desc: '–ù–∞–π—Ç–∏ 3 –±–∞–≥–∞ –∑–∞ 5 —Å–µ–∫—É–Ω–¥', icon: 'üöÄ', unlocked: false },
    { id: 4, name: '–≠–∫—Å–ø–µ—Ä—Ç', desc: '–ü—Ä–æ–π–¥–∏ —É—Ä–æ–≤–µ–Ω—å "–≠–∫—Å–ø–µ—Ä—Ç"', icon: 'üèÜ', unlocked: false },
    { id: 5, name: '–ö–æ–ª–ª–µ–∫—Ü–∏–æ–Ω–µ—Ä', desc: '–ù–∞–π—Ç–∏ –≤—Å–µ —Ç–∏–ø—ã –±–∞–≥–æ–≤', icon: 'üìö', unlocked: false },
    { id: 6, name: '–ë–µ–∑ –ø–æ–¥—Å–∫–∞–∑–æ–∫', desc: '–ü—Ä–æ–π–¥–∏ —É—Ä–æ–≤–µ–Ω—å –±–µ–∑ –ø–æ–¥—Å–∫–∞–∑–æ–∫', icon: 'üß†', unlocked: false }
  ];

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –±–∞–≥–æ–≤ –¥–ª—è —Ç–µ–∫—É—â–µ–≥–æ —É—Ä–æ–≤–Ω—è
  const generateBugs = () => {
    const { bugs: bugCount } = difficultySettings[difficulty];
    const bugs = [];
    const usedPositions = new Set();
    
    for (let i = 0; i < bugCount; i++) {
      let x, y;
      let attempts = 0;
      
      // –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º —É–Ω–∏–∫–∞–ª—å–Ω—ã–µ –ø–æ–∑–∏—Ü–∏–∏
      do {
        x = 10 + Math.random() * 80;
        y = 10 + Math.random() * 80;
        attempts++;
      } while (
        usedPositions.has(`${Math.floor(x)}-${Math.floor(y)}`) && 
        attempts < 100
      );
      
      usedPositions.add(`${Math.floor(x)}-${Math.floor(y)}`);
      
      // –í—ã–±–∏—Ä–∞–µ–º —Ç–∏–ø –±–∞–≥–∞ (—Ä–µ–¥–∫–∏–µ –ø–æ—è–≤–ª—è—é—Ç—Å—è —Ä–µ–∂–µ)
      const isRare = Math.random() < 0.2;
      const availableTypes = bugTypes.filter(type => type.rare === isRare);
      const bugType = availableTypes[Math.floor(Math.random() * availableTypes.length)];
      
      bugs.push({
        id: Date.now() + i,
        type: bugType,
        x,
        y,
        found: false,
        blinking: Math.random() < 0.3, // –ù–µ–∫–æ—Ç–æ—Ä—ã–µ –º–∏–≥–∞—é—Ç
        rotating: Math.random() < 0.2, // –ù–µ–∫–æ—Ç–æ—Ä—ã–µ –≤—Ä–∞—â–∞—é—Ç—Å—è
        size: difficultySettings[difficulty].bugSize * (isRare ? 1.2 : 1)
      });
    }
    
    return bugs;
  };

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∏–≥—Ä—ã
  const [bugs, setBugs] = useState(generateBugs());

  // –¢–∞–π–º–µ—Ä
  useEffect(() => {
    if (timeLeft > 0 && gameStatus === 'playing') {
      const timer = setTimeout(() => {
        setTimeLeft(timeLeft - 1);
        
        // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ —É–º–µ–Ω—å—à–µ–Ω–∏–µ –±–∞–≥–æ–≤ –Ω–∞ —É—Ä–æ–≤–Ω–µ "–≠–∫—Å–ø–µ—Ä—Ç"
        if (difficulty === 'expert' && timeLeft % 10 === 0) {
          setBugs(prev => prev.filter(bug => !bug.found).slice(0, -1));
        }
      }, 1000);
      
      return () => clearTimeout(timer);
    } else if (timeLeft === 0 && gameStatus === 'playing') {
      setGameStatus('lose');
    }
  }, [timeLeft, gameStatus, difficulty]);

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–π
  useEffect(() => {
    const newAchievements = [...achievements];
    
    // –ù–æ–≤–∏—á–æ–∫
    if (foundBugs.length > 0 && !achievements.find(a => a.id === 1)) {
      newAchievements.push(allAchievements[0]);
    }
    
    // –ö–æ–º–±–∏–Ω–∞—Ç–æ—Ä
    if (combo >= 5 && !achievements.find(a => a.id === 2)) {
      newAchievements.push(allAchievements[1]);
    }
    
    // –≠–∫—Å–ø–µ—Ä—Ç
    if (difficulty === 'expert' && !achievements.find(a => a.id === 4)) {
      newAchievements.push(allAchievements[3]);
    }
    
    setAchievements(newAchievements);
  }, [foundBugs.length, combo, difficulty]);

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∫–ª–∏–∫–∞ –ø–æ –±–∞–≥—É
  const handleBugClick = (bugId) => {
    if (gameStatus !== 'playing') return;
    
    const now = Date.now();
    const timeDiff = now - lastClickTime;
    
    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ–º–±–æ (–∫–ª–∏–∫–∏ –±—ã—Å—Ç—Ä–µ–µ 500–º—Å)
    if (timeDiff < 500) {
      setCombo(prev => prev + 1);
    } else {
      setCombo(1);
    }
    
    setLastClickTime(now);
    
    // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ –±–∞–≥–∞
    setBugs(prev => prev.map(bug => 
      bug.id === bugId ? { ...bug, found: true } : bug
    ));
    
    // –î–æ–±–∞–≤–ª—è–µ–º –≤ –Ω–∞–π–¥–µ–Ω–Ω—ã–µ
    const clickedBug = bugs.find(b => b.id === bugId);
    setFoundBugs(prev => [...prev, clickedBug]);
    
    // –ù–∞—á–∏—Å–ª—è–µ–º –æ—á–∫–∏
    const points = clickedBug.type.points * (1 + combo * 0.1);
    setScore(prev => Math.floor(prev + points));
    
    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ–±–µ–¥—ã
    const remainingBugs = bugs.filter(b => !b.found);
    if (remainingBugs.length === 1) {
      setGameStatus('win');
      setShowConfetti(true);
      
      // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ø—Ä–æ–º–æ–∫–æ–¥–∞
      const availableCodes = validPromoCodes.filter(code => !promoCodes.includes(code.code));
      if (availableCodes.length > 0) {
        const randomCode = availableCodes[Math.floor(Math.random() * availableCodes.length)];
        setPromoCodes(prev => [...prev, randomCode.code]);
      }
      
      // –£—Ä–æ–≤–µ–Ω—å –ø—Ä–æ–π–¥–µ–Ω - —É–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å–ª–æ–∂–Ω–æ—Å—Ç—å
      setTimeout(() => {
        if (difficulty === 'easy') setDifficulty('normal');
        else if (difficulty === 'normal') setDifficulty('hard');
        else if (difficulty === 'hard') setDifficulty('expert');
        else {
          setLevel(prev => prev + 1);
          setDifficulty('easy');
        }
        
        resetGame();
      }, 3000);
    }
  };

  // –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø–æ–¥—Å–∫–∞–∑–∫—É
  const useHint = () => {
    if (hintsUsed >= 3) return;
    
    const unfoundBugs = bugs.filter(b => !b.found);
    if (unfoundBugs.length > 0) {
      const randomBug = unfoundBugs[Math.floor(Math.random() * unfoundBugs.length)];
      
      // –ü–æ–¥—Å–≤–µ—á–∏–≤–∞–µ–º –±–∞–≥
      setSpecialBugs(prev => [...prev, {
        bugId: randomBug.id,
        type: 'hint',
        expires: Date.now() + 3000 // 3 —Å–µ–∫—É–Ω–¥—ã
      }]);
      
      setHintsUsed(prev => prev + 1);
      setScore(prev => Math.max(0, prev - 50)); // –®—Ç—Ä–∞—Ñ –∑–∞ –ø–æ–¥—Å–∫–∞–∑–∫—É
    }
  };

  // –°–±—Ä–æ—Å –∏–≥—Ä—ã
  const resetGame = () => {
    setBugs(generateBugs());
    setFoundBugs([]);
    setTimeLeft(difficultySettings[difficulty].time);
    setGameStatus('playing');
    setShowConfetti(false);
    setCombo(0);
    setSpecialBugs([]);
  };

  // –ù–∞—á–∞—Ç—å –Ω–æ–≤—É—é –∏–≥—Ä—É —Å –≤—ã–±—Ä–∞–Ω–Ω–æ–π —Å–ª–æ–∂–Ω–æ—Å—Ç—å—é
  const startNewGame = (newDifficulty) => {
    setDifficulty(newDifficulty);
    setLevel(1);
    setScore(0);
    setPromoCodes([]);
    setHintsUsed(0);
    setAchievements([]);
    resetGame();
  };

  // –ö–æ–ø–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥ –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞
  const copyPromoCode = (code) => {
    navigator.clipboard.writeText(code);
    alert(`–ü—Ä–æ–º–æ–∫–æ–¥ ${code} —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞!`);
  };

  // –†–∞—Å—á–µ—Ç –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
  const progress = bugs.length > 0 ? (foundBugs.length / bugs.length) * 100 : 0;

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      style={{
        minHeight: 'calc(100vh - 70px)',
        background: 'linear-gradient(135deg, #000000 0%, #001a1a 50%, #000000 100%)',
        padding: '20px',
        color: 'white',
        position: 'relative',
        overflow: 'hidden'
      }}
    >
      {showConfetti && <Confetti recycle={false} numberOfPieces={200} onConfettiComplete={() => setShowConfetti(false)} />}
      
      {/* –ê–Ω–∏–º–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Ñ–æ–Ω */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: `
          radial-gradient(circle at 20% 30%, rgba(0, 255, 255, 0.1) 0%, transparent 50%),
          radial-gradient(circle at 80% 70%, rgba(255, 0, 255, 0.1) 0%, transparent 50%)
        `,
        zIndex: 0
      }} />

      <div style={{ maxWidth: '1200px', margin: '0 auto', position: 'relative', zIndex: 1 }}>
        
        {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ –∏ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <motion.h1
            initial={{ y: -50 }}
            animate={{ y: 0 }}
            style={{
              fontSize: '48px',
              background: 'linear-gradient(45deg, #00ff00, #00cc00)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              marginBottom: '10px',
              textShadow: '0 0 15px rgba(0, 255, 0, 0.3)'
            }}
          >
            üéÆ –ù–ê–ô–î–ò –û–®–ò–ë–ö–£ - –£–°–õ–û–ñ–ù–ï–ù–ù–ê–Ø –í–ï–†–°–ò–Ø
          </motion.h1>
          
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            gap: '20px', 
            flexWrap: 'wrap',
            marginBottom: '20px'
          }}>
            <div style={{
              background: 'rgba(0, 255, 0, 0.1)',
              border: '2px solid #00ff00',
              borderRadius: '15px',
              padding: '15px 25px',
              minWidth: '120px'
            }}>
              <div style={{ fontSize: '12px', color: '#88ff88' }}>–£–†–û–í–ï–ù–¨</div>
              <div style={{ fontSize: '28px', color: '#00ff00', fontWeight: 'bold' }}>{level}</div>
            </div>
            
            <div style={{
              background: 'rgba(255, 255, 0, 0.1)',
              border: '2px solid #ffff00',
              borderRadius: '15px',
              padding: '15px 25px',
              minWidth: '120px'
            }}>
              <div style={{ fontSize: '12px', color: '#ffff88' }}>–û–ß–ö–ò</div>
              <div style={{ fontSize: '28px', color: '#ffff00', fontWeight: 'bold' }}>{score}</div>
            </div>
            
            <div style={{
              background: 'rgba(255, 0, 0, 0.1)',
              border: '2px solid #ff0000',
              borderRadius: '15px',
              padding: '15px 25px',
              minWidth: '120px'
            }}>
              <div style={{ fontSize: '12px', color: '#ff8888' }}>–í–†–ï–ú–Ø</div>
              <div style={{ 
                fontSize: '28px', 
                color: timeLeft > 20 ? '#ff0000' : '#ff0000',
                fontWeight: 'bold',
                animation: timeLeft <= 10 ? 'pulse 1s infinite' : 'none'
              }}>
                {timeLeft}—Å
              </div>
            </div>
            
            <div style={{
              background: 'rgba(255, 0, 255, 0.1)',
              border: '2px solid #ff00ff',
              borderRadius: '15px',
              padding: '15px 25px',
              minWidth: '120px'
            }}>
              <div style={{ fontSize: '12px', color: '#ff88ff' }}>–ö–û–ú–ë–û</div>
              <div style={{ fontSize: '28px', color: '#ff00ff', fontWeight: 'bold' }}>x{combo}</div>
            </div>
          </div>
          
          {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –±–∞—Ä */}
          <div style={{
            background: 'rgba(255, 255, 255, 0.1)',
            borderRadius: '10px',
            height: '10px',
            margin: '0 auto 20px',
            maxWidth: '600px',
            overflow: 'hidden'
          }}>
            <motion.div
              initial={{ width: 0 }}
              animate={{ width: `${progress}%` }}
              transition={{ duration: 0.5 }}
              style={{
                height: '100%',
                background: 'linear-gradient(90deg, #00ff00, #00cc00)',
                borderRadius: '10px'
              }}
            />
          </div>
        </div>

        {/* –í—ã–±–æ—Ä —Å–ª–æ–∂–Ω–æ—Å—Ç–∏ */}
        {gameStatus === 'playing' && (
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '10px',
            marginBottom: '20px',
            flexWrap: 'wrap'
          }}>
            {['easy', 'normal', 'hard', 'expert'].map((diff) => (
              <motion.button
                key={diff}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => startNewGame(diff)}
                style={{
                  background: difficulty === diff 
                    ? 'linear-gradient(45deg, #ff0000, #ff3333)' 
                    : 'rgba(255, 255, 255, 0.1)',
                  border: `2px solid ${difficulty === diff ? '#ff0000' : '#666'}`,
                  color: difficulty === diff ? 'white' : '#aaa',
                  padding: '10px 20px',
                  borderRadius: '25px',
                  fontSize: '14px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  textTransform: 'uppercase'
                }}
              >
                {diff === 'easy' ? 'ü§™ –õ–µ–≥–∫–æ' :
                 diff === 'normal' ? 'üòé –ù–æ—Ä–º–∞–ª—å–Ω–æ' :
                 diff === 'hard' ? 'üò§ –°–ª–æ–∂–Ω–æ' : 'üòà –≠–∫—Å–ø–µ—Ä—Ç'}
              </motion.button>
            ))}
          </div>
        )}

        {/* –ò–≥—Ä–æ–≤–æ–µ –ø–æ–ª–µ */}
        <div 
          ref={gameAreaRef}
          style={{
            position: 'relative',
            background: 'rgba(0, 50, 50, 0.3)',
            border: '4px solid #00aaaa',
            borderRadius: '20px',
            height: '500px',
            margin: '0 auto 30px',
            overflow: 'hidden',
            backgroundImage: `
              linear-gradient(45deg, rgba(0, 255, 255, 0.05) 25%, transparent 25%),
              linear-gradient(-45deg, rgba(0, 255, 255, 0.05) 25%, transparent 25%),
              linear-gradient(45deg, transparent 75%, rgba(0, 255, 255, 0.05) 75%),
              linear-gradient(-45deg, transparent 75%, rgba(0, 255, 255, 0.05) 75%)
            `,
            backgroundSize: '20px 20px'
          }}
        >
          {/* –ë–∞–≥–∏ */}
          <AnimatePresence>
            {bugs.map((bug) => (
              !bug.found && (
                <motion.div
                  key={bug.id}
                  initial={{ scale: 0, opacity: 0 }}
                  animate={{ 
                    scale: bug.blinking ? [1, 1.2, 1] : 1,
                    opacity: 1,
                    rotate: bug.rotating ? 360 : 0
                  }}
                  transition={{
                    scale: bug.blinking ? { repeat: Infinity, duration: 0.5 } : {},
                    rotate: bug.rotating ? { repeat: Infinity, duration: 2 } : {}
                  }}
                  whileHover={{ scale: 1.3 }}
                  whileTap={{ scale: 0.7 }}
                  onClick={() => handleBugClick(bug.id)}
                  style={{
                    position: 'absolute',
                    left: `${bug.x}%`,
                    top: `${bug.y}%`,
                    width: `${bug.size}px`,
                    height: `${bug.size}px`,
                    background: `radial-gradient(circle, ${bug.type.color}, ${bug.type.color}44)`,
                    borderRadius: bug.type.rare ? '30%' : '50%',
                    cursor: 'pointer',
                    border: `3px solid ${bug.type.color}`,
                    boxShadow: `
                      0 0 20px ${bug.type.color},
                      inset 0 0 10px rgba(255, 255, 255, 0.5)
                    `,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'white',
                    fontWeight: 'bold',
                    fontSize: `${bug.size * 0.4}px`,
                    zIndex: 2,
                    transform: 'translate(-50%, -50%)'
                  }}
                >
                  {bug.type.emoji}
                </motion.div>
              )
            ))}
          </AnimatePresence>

          {/* –ü–æ–¥—Å–∫–∞–∑–∫–∏ (–ø–æ–¥—Å–≤–µ—Ç–∫–∞ –±–∞–≥–æ–≤) */}
          {specialBugs.map((special, index) => {
            const bug = bugs.find(b => b.id === special.bugId);
            if (!bug || bug.found || Date.now() > special.expires) return null;
            
            return (
              <motion.div
                key={index}
                animate={{ 
                  boxShadow: ['0 0 30px #ffff00', '0 0 60px #ffff00', '0 0 30px #ffff00']
                }}
                transition={{ repeat: Infinity, duration: 1 }}
                style={{
                  position: 'absolute',
                  left: `${bug.x}%`,
                  top: `${bug.y}%`,
                  width: `${bug.size * 2}px`,
                  height: `${bug.size * 2}px`,
                  borderRadius: '50%',
                  border: '3px dashed #ffff00',
                  transform: 'translate(-50%, -50%)',
                  pointerEvents: 'none'
                }}
              />
            );
          })}
        </div>

        {/* –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '20px',
          marginBottom: '30px',
          flexWrap: 'wrap'
        }}>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={useHint}
            disabled={hintsUsed >= 3 || gameStatus !== 'playing'}
            style={{
              background: hintsUsed >= 3 
                ? 'linear-gradient(45deg, #666, #888)' 
                : 'linear-gradient(45deg, #ffff00, #ffaa00)',
              border: 'none',
              color: hintsUsed >= 3 ? '#aaa' : 'black',
              padding: '12px 25px',
              borderRadius: '10px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: hintsUsed >= 3 ? 'not-allowed' : 'pointer',
              opacity: hintsUsed >= 3 ? 0.7 : 1
            }}
          >
            üí° –ü–æ–¥—Å–∫–∞–∑–∫–∞ ({3 - hintsUsed} –æ—Å—Ç–∞–ª–æ—Å—å) -50 –æ—á–∫–æ–≤
          </motion.button>
          
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={resetGame}
            style={{
              background: 'linear-gradient(45deg, #ff0000, #ff3333)',
              border: 'none',
              color: 'white',
              padding: '12px 25px',
              borderRadius: '10px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: 'pointer'
            }}
          >
            üîÑ –ù–∞—á–∞—Ç—å —É—Ä–æ–≤–µ–Ω—å –∑–∞–Ω–æ–≤–æ
          </motion.button>
          
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => startNewGame('easy')}
            style={{
              background: 'linear-gradient(45deg, #00aa00, #00ff00)',
              border: 'none',
              color: 'white',
              padding: '12px 25px',
              borderRadius: '10px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: 'pointer'
            }}
          >
            üéÆ –ù–æ–≤–∞—è –∏–≥—Ä–∞
          </motion.button>
        </div>

        {/* –†–µ–∑—É–ª—å—Ç–∞—Ç –∏–≥—Ä—ã */}
        <AnimatePresence>
          {gameStatus === 'win' && (
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0 }}
              style={{
                background: 'linear-gradient(45deg, rgba(0,255,0,0.2), rgba(0,200,0,0.1))',
                border: '3px solid #00ff00',
                borderRadius: '20px',
                padding: '30px',
                marginBottom: '30px',
                textAlign: 'center'
              }}
            >
              <h2 style={{ 
                color: '#00ff00', 
                fontSize: '36px',
                marginBottom: '20px'
              }}>
                üèÜ –£–†–û–í–ï–ù–¨ –ü–†–û–ô–î–ï–ù!
              </h2>
              
              <div style={{ 
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: '20px',
                marginBottom: '30px'
              }}>
                <div>
                  <div style={{ color: '#88ff88', fontSize: '14px' }}>–°–õ–û–ñ–ù–û–°–¢–¨</div>
                  <div style={{ color: '#00ff00', fontSize: '24px', fontWeight: 'bold' }}>
                    {difficulty.toUpperCase()}
                  </div>
                </div>
                <div>
                  <div style={{ color: '#88ff88', fontSize: '14px' }}>–û–ß–ö–û–í –ó–ê–†–ê–ë–û–¢–ê–ù–û</div>
                  <div style={{ color: '#00ff00', fontSize: '24px', fontWeight: 'bold' }}>
                    {score}
                  </div>
                </div>
                <div>
                  <div style={{ color: '#88ff88', fontSize: '14px' }}>–í–†–ï–ú–Ø –û–°–¢–ê–õ–û–°–¨</div>
                  <div style={{ color: '#00ff00', fontSize: '24px', fontWeight: 'bold' }}>
                    {timeLeft}—Å
                  </div>
                </div>
              </div>

              {/* –ü—Ä–æ–º–æ–∫–æ–¥—ã */}
              {promoCodes.length > 0 && (
                <div style={{ 
                  background: 'rgba(0, 0, 0, 0.5)',
                  borderRadius: '15px',
                  padding: '20px',
                  margin: '20px auto',
                  maxWidth: '600px'
                }}>
                  <h3 style={{ color: '#ffff00', marginBottom: '15px' }}>üéÅ –í–ê–®–ò –ü–†–û–ú–û–ö–û–î–´:</h3>
                  <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    {promoCodes.map((code, index) => {
                      const promoInfo = validPromoCodes.find(p => p.code === code);
                      return (
                        <motion.div
                          key={index}
                          initial={{ x: -50 }}
                          animate={{ x: 0 }}
                          transition={{ delay: index * 0.1 }}
                          whileHover={{ scale: 1.02 }}
                          style={{
                            background: 'rgba(255, 255, 0, 0.1)',
                            border: '2px solid #ffff00',
                            borderRadius: '10px',
                            padding: '15px',
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center'
                          }}
                        >
                          <div>
                            <div style={{ fontSize: '24px', color: '#ffff00', fontWeight: 'bold' }}>
                              {code}
                            </div>
                            <div style={{ fontSize: '14px', color: '#ffff88' }}>
                              –°–∫–∏–¥–∫–∞ {promoInfo?.discount || 10}% –Ω–∞ –ª—é–±–æ–π –∑–∞–∫–∞–∑
                            </div>
                          </div>
                          <motion.button
                            whileHover={{ scale: 1.1 }}
                            whileTap={{ scale: 0.9 }}
                            onClick={() => copyPromoCode(code)}
                            style={{
                              background: 'linear-gradient(45deg, #ffff00, #ffaa00)',
                              border: 'none',
                              color: 'black',
                              padding: '10px 20px',
                              borderRadius: '8px',
                              fontSize: '14px',
                              fontWeight: 'bold',
                              cursor: 'pointer'
                            }}
                          >
                            üìã –ö–æ–ø–∏—Ä–æ–≤–∞—Ç—å
                          </motion.button>
                        </motion.div>
                      );
                    })}
                  </div>
                </div>
              )}

              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => {
                  const message = `–Ø –ø—Ä–æ—à—ë–ª ${level} —É—Ä–æ–≤–µ–Ω—å –≤ –∏–≥—Ä–µ "–ù–∞–π–¥–∏ –æ—à–∏–±–∫—É" —Å–æ —Å–∫–∏–¥–∫–æ–π! –ú–æ–π —Å—á—ë—Ç: ${score} –æ—á–∫–æ–≤`;
                  window.open(`https://t.me/Lyokorps?text=${encodeURIComponent(message)}`, '_blank');
                }}
                style={{
                  background: 'linear-gradient(45deg, #00aa00, #00ff00)',
                  border: 'none',
                  color: 'black',
                  padding: '15px 30px',
                  borderRadius: '10px',
                  fontSize: '18px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  marginTop: '20px'
                }}
              >
                üí¨ –°–æ–æ–±—â–∏—Ç—å –æ –ø–æ–±–µ–¥–µ –≤ Telegram
              </motion.button>
            </motion.div>
          )}

          {gameStatus === 'lose' && (
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              style={{
                background: 'linear-gradient(45deg, rgba(255,0,0,0.2), rgba(200,0,0,0.1))',
                border: '3px solid #ff0000',
                borderRadius: '20px',
                padding: '30px',
                marginBottom: '30px',
                textAlign: 'center'
              }}
            >
              <h2 style={{ 
                color: '#ff0000', 
                fontSize: '36px',
                marginBottom: '20px'
              }}>
                üíÄ –í–†–ï–ú–Ø –í–´–®–õ–û!
              </h2>
              <p style={{ fontSize: '20px', color: '#ff8888', marginBottom: '30px' }}>
                –í—ã –Ω–∞—à–ª–∏ {foundBugs.length} –∏–∑ {bugs.length} –æ—à–∏–±–æ–∫
              </p>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={resetGame}
                style={{
                  background: 'linear-gradient(45deg, #ff0000, #ff3333)',
                  border: 'none',
                  color: 'white',
                  padding: '15px 30px',
                  borderRadius: '10px',
                  fontSize: '18px',
                  fontWeight: 'bold',
                  cursor: 'pointer'
                }}
              >
                üîÑ –ü–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å —Å–Ω–æ–≤–∞
              </motion.button>
            </motion.div>
          )}
        </AnimatePresence>

        {/* –î–æ—Å—Ç–∏–∂–µ–Ω–∏—è */}
        {achievements.length > 0 && (
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            style={{
              background: 'rgba(255, 255, 0, 0.05)',
              border: '2px solid rgba(255, 255, 0, 0.3)',
              borderRadius: '20px',
              padding: '25px',
              marginBottom: '30px'
            }}
          >
            <h3 style={{ 
              color: '#ffff00', 
              fontSize: '24px',
              marginBottom: '20px',
              textAlign: 'center'
            }}>
              üèÖ –í–ê–®–ò –î–û–°–¢–ò–ñ–ï–ù–ò–Ø
            </h3>
            <div style={{ 
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
              gap: '15px'
            }}>
              {achievements.map((achievement) => (
                <motion.div
                  key={achievement.id}
                  whileHover={{ scale: 1.03 }}
                  style={{
                    background: 'rgba(255, 255, 0, 0.1)',
                    border: '1px solid rgba(255, 255, 0, 0.5)',
                    borderRadius: '15px',
                    padding: '15px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '15px'
                  }}
                >
                  <span style={{ fontSize: '32px' }}>{achievement.icon}</span>
                  <div>
                    <div style={{ color: '#ffff00', fontWeight: 'bold' }}>{achievement.name}</div>
                    <div style={{ color: '#ffff88', fontSize: '14px' }}>{achievement.desc}</div>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        )}

        {/* –¢–∏–ø—ã –±–∞–≥–æ–≤ */}
        <div style={{
          background: 'rgba(0, 100, 255, 0.1)',
          border: '1px solid rgba(0, 100, 255, 0.3)',
          borderRadius: '20px',
          padding: '25px'
        }}>
          <h3 style={{ 
            color: '#66ccff', 
            fontSize: '24px',
            marginBottom: '20px',
            textAlign: 'center'
          }}>
            üéØ –¢–ò–ü–´ –û–®–ò–ë–û–ö –ò –ë–ê–õ–õ–´
          </h3>
          <div style={{ 
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
            gap: '15px'
          }}>
            {bugTypes.map((bugType) => (
              <div
                key={bugType.id}
                style={{
                  background: 'rgba(0, 100, 255, 0.2)',
                  border: `2px solid ${bugType.color}`,
                  borderRadius: '10px',
                  padding: '15px',
                  textAlign: 'center'
                }}
              >
                <div style={{ fontSize: '32px' }}>{bugType.emoji}</div>
                <div style={{ 
                  color: bugType.color, 
                  fontWeight: 'bold',
                  margin: '10px 0 5px'
                }}>
                  {bugType.name}
                </div>
                <div style={{ color: '#aaddff' }}>{bugType.points} –æ—á–∫–æ–≤</div>
                {bugType.rare && (
                  <div style={{
                    background: '#ff00ff',
                    color: 'white',
                    fontSize: '10px',
                    padding: '3px 8px',
                    borderRadius: '10px',
                    marginTop: '5px',
                    display: 'inline-block'
                  }}>
                    –†–ï–î–ö–ò–ô
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes pulse {
            0% { opacity: 1; }
            50% { opacity: 0.5; }
            100% { opacity: 1; }
          }
        `}
      </style>
    </motion.div>
  );
};

export default GamePage;
