                <>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={copyPromoCode}
                    style={{
                      background: 'linear-gradient(45deg, #ffff00, #ffaa00)',
                      border: 'none',
                      color: 'black',
                      padding: '15px 30px',
                      borderRadius: '10px',
                      fontSize: '18px',
                      fontWeight: 'bold',
                      cursor: 'pointer'
                    }}
                  >
                    üìã –°–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥
                  </motion.button>
                  
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={openTelegram}
                    style={{
                      background: 'linear-gradient(45deg, #0088cc, #00aaff)',
                      border: 'none',
                      color: 'white',
                      padding: '15px 30px',
                      borderRadius: '10px',
                      fontSize: '18px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      gap: '10px'
                    }}
                  >
                    <span>üí¨</span>
                    –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥ –≤ Telegram
                  </motion.button>
                </>
              )}

              {(gameStatus === 'completed' || gameStatus === 'win') && promoCode && (
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={openTelegram}
                  style={{
                    background: 'linear-gradient(45deg, #0088cc, #00aaff)',
                    border: 'none',
                    color: 'white',
                    padding: '15px 30px',
                    borderRadius: '10px',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '10px'
                  }}
                >
                  <span>üí¨</span>
                  –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥ –≤ Telegram
                </motion.button>
              )}

              {gameStatus === 'lose' && (
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => {
                    setGameStatus('playing');
                    resetGame();
                  }}
                  style={{
                    background: 'linear-gradient(45deg, #ff0000, #ff3333)',
                    border: 'none',
                    color: 'white',
                    padding: '15px 30px',
                    borderRadius: '10px',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    cursor: 'pointer'
                  }}
                >
                  üîÑ –ü–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å —Å–Ω–æ–≤–∞
                </motion.button>
              )}

              {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è –≤–æ–∑–≤—Ä–∞—Ç–∞ –Ω–∞ —Å–∞–π—Ç */}
              <button
                onClick={returnToMainSite}
                style={{
                  background: 'transparent',
                  border: '2px solid #666',
                  color: '#999',
                  padding: '10px 20px',
                  borderRadius: '10px',
                  fontSize: '14px',
                  cursor: 'pointer',
                  marginTop: '10px'
                }}
              >
                –í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞ —Å–∞–π—Ç
              </button>

              {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è —Ç–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏—è (–æ—á–∏—Å—Ç–∏—Ç—å localStorage) */}
              {process.env.NODE_ENV === 'development' && (
                <button
                  onClick={clearLocalStorage}
                  style={{
                    background: 'rgba(255, 0, 0, 0.1)',
                    border: '1px dashed #ff0000',
                    color: '#ff6666',
                    padding: '8px 16px',
                    borderRadius: '8px',
                    fontSize: '12px',
                    cursor: 'pointer',
                    marginTop: '20px',
                    opacity: 0.7
                  }}
                >
                  üß™ –û—á–∏—Å—Ç–∏—Ç—å –∏ –Ω–∞—á–∞—Ç—å –∑–∞–Ω–æ–≤–æ (–¥–ª—è —Ç–µ—Å—Ç–∞)
                </button>
              )}
            </div>
          </motion.div>
        )}
      </div>

      <style>
        {`
          @keyframes pulse {
            0% { opacity: 1; }
            50% { opacity: 0.5; }
            100% { opacity: 1; }
          }
        `}
      </style>
    </motion.div>
  );
};

export default GamePage;
