import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const GamePage = () => {
  const [foundBugs, setFoundBugs] = useState([]);
  const [promoCode, setPromoCode] = useState('');
  const [gameCompleted, setGameCompleted] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [showHint, setShowHint] = useState(false);
  
  const bugs = [
    { id: 1, name: '–°–ª–æ–º–∞–Ω–Ω—ã–π —á–∏–ø', x: 15, y: 20, hint: '–ò—â–∏ –≤–æ–∑–ª–µ –º–æ–Ω–∏—Ç–æ—Ä–∞' },
    { id: 2, name: '–ü–µ—Ä–µ–≥–æ—Ä–µ–≤—à–∏–π —Å–≤–µ—Ç–æ–¥–∏–æ–¥', x: 65, y: 40, hint: '–ü—Ä–∞–≤–∞—è —Å—Ç–æ—Ä–æ–Ω–∞ –ø–ª–∞—Ç—ã' },
    { id: 3, name: '–û—à–∏–±–∫–∞ –≤ –∫–æ–¥–µ', x: 35, y: 70, hint: '–ù–∞ —ç–∫—Ä–∞–Ω–µ –Ω–æ—É—Ç–±—É–∫–∞' },
    { id: 4, name: '–û–±—Ä—ã–≤ –ø—Ä–æ–≤–æ–¥–∞', x: 80, y: 25, hint: '–í–æ–∑–ª–µ –±–ª–æ–∫–∞ –ø–∏—Ç–∞–Ω–∏—è' },
    { id: 5, name: '–ü–µ—Ä–µ–≥—Ä–µ–≤ –ø—Ä–æ—Ü–µ—Å—Å–æ—Ä–∞', x: 25, y: 55, hint: '–¶–µ–Ω—Ç—Ä —Å–∏—Å—Ç–µ–º–Ω–æ–≥–æ –±–ª–æ–∫–∞' }
  ];

  const promoWords = {
    ru: ['–¢–ï–•–ù–ò–ö', '–ú–ê–°–¢–ï–†', '–≠–ö–°–ü–ï–†–¢', '–ü–†–û–§–ò', '–ì–£–†–£', '–í–ò–†–¢–£–û–ó', '–ê–°', '–ì–ï–ù–ò–ô', '–ü–†–û–†–ê–ë', '–°–ü–ï–¶'],
    en: ['TECH', 'GEEK', 'NINJA', 'WIZARD', 'PRO', 'BOSS', 'GURU', 'MASTER', 'EXPERT', 'ACE']
  };

  const generatePromoCode = () => {
    const lang = Math.random() > 0.5 ? 'ru' : 'en';
    const word = promoWords[lang][Math.floor(Math.random() * promoWords[lang].length)];
    const number = Math.floor(Math.random() * 90) + 10;
    return `${word}${number}`;
  };

  const handleBugClick = (bugId) => {
    if (!foundBugs.includes(bugId)) {
      setFoundBugs([...foundBugs, bugId]);
      
      if (foundBugs.length + 1 >= 3) {
        const code = generatePromoCode();
        setPromoCode(code);
        setGameCompleted(true);
      }
    }
  };

  useEffect(() => {
    if (timeLeft > 0 && !gameCompleted) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0) {
      setShowHint(true);
    }
  }, [timeLeft, gameCompleted]);

  const resetGame = () => {
    setFoundBugs([]);
    setPromoCode('');
    setGameCompleted(false);
    setTimeLeft(60);
    setShowHint(false);
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      style={{
        minHeight: 'calc(100vh - 70px)',
        background: 'linear-gradient(135deg, #000000 0%, #001a1a 50%, #000000 100%)',
        padding: '30px 20px',
        color: 'white',
        textAlign: 'center'
      }}
    >
      <div style={{ maxWidth: '1000px', margin: '0 auto' }}>
        {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
        <motion.h1
          initial={{ y: -30 }}
          animate={{ y: 0 }}
          style={{
            fontSize: '48px',
            color: '#00ff00',
            marginBottom: '10px',
            textShadow: '0 0 15px #00ff00'
          }}
        >
          üéÆ –ù–ê–ô–î–ò –û–®–ò–ë–ö–£
        </motion.h1>
        
        <p style={{ 
          fontSize: '20px', 
          color: '#66ff66',
          marginBottom: '30px'
        }}>
          –ù–∞–π–¥–∏ 3 –æ—à–∏–±–∫–∏ –≤ —Å–∏—Å—Ç–µ–º–µ –∏ –ø–æ–ª—É—á–∏ —Å–∫–∏–¥–∫—É 10%!
        </p>

        {/* –ò–≥—Ä–æ–≤–∞—è —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          gap: '30px',
          marginBottom: '30px',
          flexWrap: 'wrap'
        }}>
          <div style={{ 
            background: 'rgba(0, 255, 0, 0.1)',
            border: '2px solid #00ff00',
            borderRadius: '15px',
            padding: '15px 25px',
            minWidth: '150px'
          }}>
            <div style={{ fontSize: '14px', color: '#66ff66' }}>–ù–ê–ô–î–ï–ù–û –û–®–ò–ë–û–ö</div>
            <div style={{ 
              fontSize: '36px', 
              color: '#00ff00',
              fontWeight: 'bold'
            }}>
              {foundBugs.length}/5
            </div>
          </div>
          
          <div style={{ 
            background: 'rgba(255, 255, 0, 0.1)',
            border: '2px solid #ffff00',
            borderRadius: '15px',
            padding: '15px 25px',
            minWidth: '150px'
          }}>
            <div style={{ fontSize: '14px', color: '#ffff66' }}>–û–°–¢–ê–õ–û–°–¨ –í–†–ï–ú–ï–ù–ò</div>
            <div style={{ 
              fontSize: '36px', 
              color: timeLeft > 20 ? '#ffff00' : '#ff0000',
              fontWeight: 'bold',
              textShadow: timeLeft <= 20 ? '0 0 10px #ff0000' : 'none'
            }}>
              {timeLeft} —Å–µ–∫
            </div>
          </div>
          
          <div style={{ 
            background: 'rgba(255, 0, 255, 0.1)',
            border: '2px solid #ff00ff',
            borderRadius: '15px',
            padding: '15px 25px',
            minWidth: '150px'
          }}>
            <div style={{ fontSize: '14px', color: '#ff66ff' }}>–°–ö–ò–î–ö–ê</div>
            <div style={{ 
              fontSize: '36px', 
              color: '#ff00ff',
              fontWeight: 'bold'
            }}>
              {gameCompleted ? '10%' : '0%'}
            </div>
          </div>
        </div>

        {/* –ò–≥—Ä–æ–≤–æ–µ –ø–æ–ª–µ */}
        <div style={{ 
          position: 'relative',
          background: 'rgba(0, 50, 50, 0.3)',
          border: '3px solid #00aaaa',
          borderRadius: '20px',
          height: '500px',
          margin: '0 auto 30px',
          overflow: 'hidden',
          backgroundImage: 'radial-gradient(circle at 50% 50%, rgba(0, 255, 255, 0.1) 0%, transparent 50%)'
        }}>
          {/* –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ —Ä–∞–±–æ—á–µ–≥–æ —Å—Ç–æ–ª–∞ —Ç–µ—Ö–Ω–∏–∫–∞ */}
          <div style={{
            position: 'absolute',
            top: '10%',
            left: '10%',
            width: '30%',
            height: '30%',
            background: 'rgba(100, 100, 100, 0.5)',
            border: '2px solid #666',
            borderRadius: '10px'
          }}>
            <div style={{
              position: 'absolute',
              top: '10%',
              left: '10%',
              right: '10%',
              bottom: '10%',
              background: '#000',
              borderRadius: '5px'
            }} />
            <div style={{
              position: 'absolute',
              top: '45%',
              left: '20%',
              right: '20%',
              height: '2px',
              background: '#666'
            }} />
          </div>

          {/* –ë–∞–≥–∏ */}
          {bugs.map((bug) => (
            <motion.div
              key={bug.id}
              initial={{ scale: 0 }}
              animate={{ 
                scale: foundBugs.includes(bug.id) ? 0 : 1,
                opacity: foundBugs.includes(bug.id) ? 0 : 1
              }}
              whileHover={{ scale: 1.2 }}
              whileTap={{ scale: 0.8 }}
              onClick={() => !foundBugs.includes(bug.id) && handleBugClick(bug.id)}
              style={{
                position: 'absolute',
                left: `${bug.x}%`,
                top: `${bug.y}%`,
                width: '40px',
                height: '40px',
                background: foundBugs.includes(bug.id) 
                  ? 'radial-gradient(circle, #00ff00, #008800)' 
                  : 'radial-gradient(circle, #ff0000, #880000)',
                borderRadius: '50%',
                cursor: 'pointer',
                border: '2px solid white',
                boxShadow: '0 0 20px currentColor',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontWeight: 'bold',
                fontSize: '20px',
                zIndex: 2
              }}
            >
              {foundBugs.includes(bug.id) ? '‚úì' : '!'}
            </motion.div>
          ))}

          {/* –ê–Ω–∏–º–∞—Ü–∏–∏ */}
          <motion.div
            animate={{ 
              boxShadow: ['0 0 10px #00ff00', '0 0 20px #00ff00', '0 0 10px #00ff00']
            }}
            transition={{ repeat: Infinity, duration: 1 }}
            style={{
              position: 'absolute',
              left: '65%',
              top: '40%',
              width: '20px',
              height: '20px',
              background: '#00ff00',
              borderRadius: '50%'
            }}
          />
        </div>

        {/* –†–µ–∑—É–ª—å—Ç–∞—Ç –∏–≥—Ä—ã */}
        {gameCompleted && (
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            style={{
              background: 'linear-gradient(45deg, rgba(0,255,0,0.2), rgba(0,200,0,0.1))',
              border: '3px solid #00ff00',
              borderRadius: '20px',
              padding: '30px',
              marginBottom: '30px'
            }}
          >
            <h2 style={{ 
              color: '#00ff00', 
              fontSize: '36px',
              marginBottom: '20px'
            }}>
              üéâ –ü–û–ó–î–†–ê–í–õ–Ø–ï–ú!
            </h2>
            
            <p style={{ 
              fontSize: '24px', 
              color: '#ccffcc',
              marginBottom: '15px'
            }}>
              –í—ã –Ω–∞—à–ª–∏ {foundBugs.length} –æ—à–∏–±–æ–∫ –∏–∑ 5!
            </p>
            
            <div style={{ 
              background: 'rgba(0, 0, 0, 0.5)',
              borderRadius: '15px',
              padding: '20px',
              margin: '20px auto',
              maxWidth: '500px'
            }}>
              <p style={{ 
                fontSize: '18px', 
                color: '#ffff00',
                marginBottom: '10px'
              }}>
                –í–∞—à –ø—Ä–æ–º–æ–∫–æ–¥ –Ω–∞ —Å–∫–∏–¥–∫—É 10%:
              </p>
              <motion.div
                animate={{ 
                  scale: [1, 1.1, 1],
                  textShadow: ['0 0 10px #00ff00', '0 0 20px #00ff00', '0 0 10px #00ff00']
                }}
                transition={{ repeat: Infinity, duration: 2 }}
                style={{
                  fontSize: '42px',
                  color: '#00ff00',
                  fontWeight: 'bold',
                  fontFamily: 'monospace',
                  letterSpacing: '3px',
                  padding: '15px',
                  background: 'rgba(0, 255, 0, 0.1)',
                  borderRadius: '10px',
                  border: '2px dashed #00ff00'
                }}
              >
                {promoCode}
              </motion.div>
            </div>
            
            <p style={{ 
              fontSize: '16px', 
              color: '#88ff88',
              marginBottom: '20px'
            }}>
              * –ü—Ä–æ–º–æ–∫–æ–¥ –¥–µ–π—Å—Ç–≤—É–µ—Ç 30 –¥–Ω–µ–π –Ω–∞ –ø–µ—Ä–≤—ã–π –∑–∞–∫–∞–∑
            </p>
            
            <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => {
                  const message = `–ó–¥—Ä–∞–≤—Å—Ç–≤—É–π—Ç–µ! –Ø –ø–æ–ª—É—á–∏–ª –ø—Ä–æ–º–æ–∫–æ–¥ –Ω–∞ —Å–∫–∏–¥–∫—É 10%: ${promoCode}`;
                  window.open(`https://t.me/Lyokorps?text=${encodeURIComponent(message)}`, '_blank');
                }}
                style={{
                  background: 'linear-gradient(45deg, #00aa00, #00ff00)',
                  border: 'none',
                  color: 'black',
                  padding: '15px 30px',
                  borderRadius: '10px',
                  fontSize: '18px',
                  fontWeight: 'bold',
                  cursor: 'pointer'
                }}
              >
                üí¨ –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥
              </motion.button>
              
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={resetGame}
                style={{
                  background: 'transparent',
                  border: '2px solid #00ff00',
                  color: '#00ff00',
                  padding: '15px 30px',
                  borderRadius: '10px',
                  fontSize: '18px',
                  fontWeight: 'bold',
                  cursor: 'pointer'
                }}
              >
                üîÑ –ò–≥—Ä–∞—Ç—å —Å–Ω–æ–≤–∞
              </motion.button>
            </div>
          </motion.div>
        )}

        {/* –ü–æ–¥—Å–∫–∞–∑–∫–∞ –ø—Ä–∏ –º–∞–ª–æ–º –≤—Ä–µ–º–µ–Ω–∏ */}
        {showHint && !gameCompleted && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            style={{
              background: 'linear-gradient(45deg, rgba(255,255,0,0.2), rgba(200,200,0,0.1))',
              border: '2px solid #ffff00',
              borderRadius: '15px',
              padding: '20px',
              marginBottom: '20px'
            }}
          >
            <h3 style={{ color: '#ffff00', marginBottom: '10px' }}>üí° –ù—É–∂–Ω–∞ –ø–æ–¥—Å–∫–∞–∑–∫–∞?</h3>
            <p style={{ color: '#ffff88' }}>
              {bugs.find(b => !foundBugs.includes(b.id))?.hint || '–ò—â–∏ –≤ —É–≥–ª–∞—Ö!'}
            </p>
          </motion.div>
        )}

        {/* –ò–Ω—Å—Ç—Ä—É–∫—Ü–∏—è */}
        <div style={{ 
          background: 'rgba(0, 100, 255, 0.1)',
          border: '1px solid #0066ff',
          borderRadius: '15px',
          padding: '20px',
          marginTop: '30px'
        }}>
          <h3 style={{ color: '#66ccff', marginBottom: '15px' }}>üéØ –ö–∞–∫ –∏–≥—Ä–∞—Ç—å:</h3>
          <div style={{ 
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '15px',
            textAlign: 'left'
          }}>
            <div style={{ color: '#aaddff' }}>
              <strong>1.</strong> –ù–∞–π–¥–∏ 3 –∫—Ä–∞—Å–Ω—ã–µ —Ç–æ—á–∫–∏ —Å –≤–æ—Å–∫–ª–∏—Ü–∞—Ç–µ–ª—å–Ω—ã–º–∏ –∑–Ω–∞–∫–∞–º–∏
            </div>
            <div style={{ color: '#aaddff' }}>
              <strong>2.</strong> –ù–∞–∂–º–∏ –Ω–∞ –Ω–∏—Ö –º—ã—à–∫–æ–π –∏–ª–∏ –ø–∞–ª—å—Ü–µ–º
            </div>
            <div style={{ color: '#aaddff' }}>
              <strong>3.</strong> –£ —Ç–µ–±—è –µ—Å—Ç—å 60 —Å–µ–∫—É–Ω–¥
            </div>
            <div style={{ color: '#aaddff' }}>
              <strong>4.</strong> –ü–æ–ª—É—á–∏ –ø—Ä–æ–º–æ–∫–æ–¥ –Ω–∞ —Å–∫–∏–¥–∫—É 10%
            </div>
          </div>
        </div>

        {/* –ö–Ω–æ–ø–∫–∞ —Å–±—Ä–æ—Å–∞ */}
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={resetGame}
          style={{
            background: 'linear-gradient(45deg, #ff0000, #ff3333)',
            border: 'none',
            color: 'white',
            padding: '12px 30px',
            borderRadius: '50px',
            fontSize: '18px',
            fontWeight: 'bold',
            cursor: 'pointer',
            marginTop: '30px'
          }}
        >
          üîÑ –ù–∞—á–∞—Ç—å –∑–∞–Ω–æ–≤–æ
        </motion.button>
      </div>
    </motion.div>
  );
};

export default GamePage;
