import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Confetti from 'react-confetti';

const GamePage = () => {
  // –°–æ—Å—Ç–æ—è–Ω–∏—è –∏–≥—Ä—ã
  const [level, setLevel] = useState(1);
  const [score, setScore] = useState(0);
  const [foundBugs, setFoundBugs] = useState([]);
  const [timeLeft, setTimeLeft] = useState(60);
  const [gameStatus, setGameStatus] = useState('rules'); // rules, playing, win, lose, gameOver
  const [showConfetti, setShowConfetti] = useState(false);
  const [promoCode, setPromoCode] = useState(null);
  const [combo, setCombo] = useState(0);
  const [comboTime, setComboTime] = useState(0);
  const [hint, setHint] = useState('');
  const [achievements, setAchievements] = useState([]);
  const [difficulty, setDifficulty] = useState('normal'); // easy, normal, hard, expert
  const [gamesPlayed, setGamesPlayed] = useState(() => {
    return parseInt(localStorage.getItem('bugGameGamesPlayed') || '0');
  });
  const [maxGamesPerDevice] = useState(1); // –û–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ 1 –∏–≥—Ä–∞ —Å —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞
  const [currentPromo, setCurrentPromo] = useState(null);
  const [levelComplete, setLevelComplete] = useState(false);
  const gameAreaRef = useRef(null);
  const winTimeoutRef = useRef(null);

  // –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–ª–æ–∂–Ω–æ—Å—Ç–∏
  const difficultySettings = {
    easy: { bugs: 3, time: 90, bugSize: 50 },
    normal: { bugs: 5, time: 60, bugSize: 40 },
    hard: { bugs: 7, time: 45, bugSize: 30 },
    expert: { bugs: 10, time: 30, bugSize: 20 }
  };

  // –¢–∏–ø—ã –±–∞–≥–æ–≤
  const bugTypes = [
    { id: 1, name: '–°–ª–æ–º–∞–Ω–Ω—ã–π —á–∏–ø', emoji: 'üíæ', color: '#ff0000', points: 100, rare: false },
    { id: 2, name: '–û—à–∏–±–∫–∞ –∫–æ–¥–∞', emoji: '‚ùå', color: '#ff6600', points: 150, rare: false },
    { id: 3, name: '–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∞—è –æ—à–∏–±–∫–∞', emoji: 'üí•', color: '#ff0066', points: 200, rare: true },
    { id: 4, name: '–ë–∞–≥ –≤ —Å–∏—Å—Ç–µ–º–µ', emoji: 'üêû', color: '#9900ff', points: 250, rare: true },
  ];

  // –ü—Ä–æ–º–æ–∫–æ–¥—ã
  const promoCodes = [
    { id: 1, code: 'BUGFINDER10', discount: 10 },
    { id: 2, code: 'DEBUGPRO20', discount: 20 },
    { id: 3, code: 'ERRORHUNTER30', discount: 30 },
    { id: 4, code: 'GAMERULES50', discount: 50 }
  ];

  // –î–æ—Å—Ç–∏–∂–µ–Ω–∏—è
  const achievementsList = [
    { id: 1, name: '–ù–æ–≤–∏—á–æ–∫', condition: () => score >= 100, icon: 'üéØ' },
    { id: 2, name: '–ö–æ–º–±–∏–Ω–∞—Ç–æ—Ä', condition: () => combo >= 5, icon: '‚ö°' },
    { id: 3, name: '–°–∫–æ—Ä–æ—Å—Ç—Ä–µ–ª', condition: () => timeLeft >= 50 && foundBugs.length > 0, icon: 'üöÄ' },
    { id: 4, name: '–≠–∫—Å–ø–µ—Ä—Ç', condition: () => level >= 3, icon: 'üèÜ' },
    { id: 5, name: '–ö–æ–ª–ª–µ–∫—Ü–∏–æ–Ω–µ—Ä', condition: () => foundBugs.length === getBugsForLevel().length, icon: 'üìö' }
  ];

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –±–∞–≥–æ–≤
  const getBugsForLevel = () => {
    const settings = difficultySettings[difficulty];
    const bugs = [];
    const totalBugs = settings.bugs;

    for (let i = 0; i < totalBugs; i++) {
      const isRare = Math.random() < 0.2; // 20% —à–∞–Ω—Å —Ä–µ–¥–∫–æ–≥–æ –±–∞–≥–∞
      const availableTypes = bugTypes.filter(type => type.rare === isRare);
      const bugType = availableTypes[Math.floor(Math.random() * availableTypes.length)];
      
      // –°–ª—É—á–∞–π–Ω—ã–µ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö –∏–≥—Ä–æ–≤–æ–π –æ–±–ª–∞—Å—Ç–∏
      const gameArea = gameAreaRef.current;
      const maxX = gameArea ? gameArea.clientWidth - settings.bugSize : 800 - settings.bugSize;
      const maxY = gameArea ? gameArea.clientHeight - settings.bugSize : 500 - settings.bugSize;
      
      const x = Math.floor(Math.random() * maxX);
      const y = Math.floor(Math.random() * maxY);
      
      bugs.push({
        id: Date.now() + i,
        type: bugType,
        x,
        y,
        found: false,
        blinking: Math.random() < 0.3, // 30% –±–∞–≥–æ–≤ –º–∏–≥–∞—é—Ç
        spinning: Math.random() < 0.2, // 20% –±–∞–≥–æ–≤ –≤—Ä–∞—â–∞—é—Ç—Å—è
        hidden: difficulty === 'expert' && Math.random() < 0.2 // –í —ç–∫—Å–ø–µ—Ä—Ç–Ω–æ–º —Ä–µ–∂–∏–º–µ 20% –±–∞–≥–æ–≤ —Å–∫—Ä—ã—Ç—ã
      });
    }
    return bugs;
  };

  const [bugs, setBugs] = useState([]);

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∏–≥—Ä—ã
  useEffect(() => {
    setBugs(getBugsForLevel());
  }, [level, difficulty]);

  // –¢–∞–π–º–µ—Ä
  useEffect(() => {
    let timer;
    if (gameStatus === 'playing' && timeLeft > 0) {
      timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    } else if (timeLeft === 0 && gameStatus === 'playing') {
      setGameStatus('lose');
    }
    return () => clearTimeout(timer);
  }, [timeLeft, gameStatus]);

  // –ö–æ–º–±–æ-—Ç–∞–π–º–µ—Ä
  useEffect(() => {
    let comboTimer;
    if (comboTime > 0) {
      comboTimer = setTimeout(() => setComboTime(comboTime - 1), 1000);
    } else if (comboTime === 0 && combo > 0) {
      setCombo(0);
    }
    return () => clearTimeout(comboTimer);
  }, [comboTime]);

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–π
  useEffect(() => {
    const newAchievements = achievementsList.filter(ach => 
      !achievements.includes(ach.id) && ach.condition()
    );
    if (newAchievements.length > 0) {
      setAchievements(prev => [...prev, ...newAchievements.map(a => a.id)]);
    }
  }, [score, combo, timeLeft, level, foundBugs]);

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞—Ö–æ–∂–¥–µ–Ω–∏—è –±–∞–≥–∞
  const handleBugClick = (bugId) => {
    if (gameStatus !== 'playing') return;

    const updatedBugs = bugs.map(bug => 
      bug.id === bugId ? { ...bug, found: true } : bug
    );
    
    const foundBug = updatedBugs.find(bug => bug.id === bugId);
    if (foundBug && !foundBug.found) {
      // –û–±–Ω–æ–≤–ª—è–µ–º –Ω–∞–π–¥–µ–Ω–Ω—ã–µ –±–∞–≥–∏
      const newFoundBugs = [...foundBugs, bugId];
      setFoundBugs(newFoundBugs);

      // –ù–∞—á–∏—Å–ª–µ–Ω–∏–µ –æ—á–∫–æ–≤
      let points = foundBug.type.points;
      if (combo > 0) {
        points = Math.floor(points * (1 + combo * 0.2)); // 20% –∑–∞ –∫–∞–∂–¥—ã–π –∫–æ–º–±–æ
      }
      setScore(prev => prev + points);

      // –£–≤–µ–ª–∏—á–µ–Ω–∏–µ –∫–æ–º–±–æ
      setCombo(prev => prev + 1);
      setComboTime(5); // 5 —Å–µ–∫—É–Ω–¥ –Ω–∞ —Å–ª–µ–¥—É—é—â–∏–π –∫–ª–∏–∫ –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –∫–æ–º–±–æ

      // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ–±–µ–¥—ã
      if (newFoundBugs.length === bugs.length) {
        winTimeoutRef.current = setTimeout(() => {
          handleWin();
        }, 1000);
      }
    }
    
    setBugs(updatedBugs);
  };

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –ø–æ–±–µ–¥—ã
  const handleWin = () => {
    if (gamesPlayed >= maxGamesPerDevice) {
      setGameStatus('gameOver');
      return;
    }

    setShowConfetti(true);
    setTimeout(() => setShowConfetti(false), 5000);

    // –í—ã–±–æ—Ä —Å–ª—É—á–∞–π–Ω–æ–≥–æ –ø—Ä–æ–º–æ–∫–æ–¥–∞
    const randomPromo = promoCodes[Math.floor(Math.random() * promoCodes.length)];
    setPromoCode(randomPromo);
    setCurrentPromo(randomPromo);

    // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ —Å—á–µ—Ç—á–∏–∫–∞ –∏–≥—Ä
    const newGamesPlayed = gamesPlayed + 1;
    setGamesPlayed(newGamesPlayed);
    localStorage.setItem('bugGameGamesPlayed', newGamesPlayed.toString());

    setGameStatus('win');
  };

  // –°–±—Ä–æ—Å –∏–≥—Ä—ã
  const resetGame = () => {
    setFoundBugs([]);
    setTimeLeft(difficultySettings[difficulty].time);
    setBugs(getBugsForLevel());
    setCombo(0);
    setComboTime(0);
    setHint('');
    setLevelComplete(false);
  };

  // –ù–∞—á–∞—Ç—å –Ω–æ–≤—É—é –∏–≥—Ä—É
  const startNewGame = (newDifficulty = difficulty) => {
    if (gamesPlayed >= maxGamesPerDevice) {
      setGameStatus('gameOver');
      return;
    }
    setDifficulty(newDifficulty);
    setLevel(1);
    setGameStatus('playing');
    resetGame();
    setPromoCode(null);
  };

  // –ö–æ–ø–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥
  const copyPromoCode = () => {
