    }
  };

  // –ü–æ–∫–∞–∑–∞—Ç—å –ø–æ–¥—Å–∫–∞–∑–∫—É
  const showHintForBug = () => {
    if (combo > 0) {
      // –®—Ç—Ä–∞—Ñ –∑–∞ –ø–æ–¥—Å–∫–∞–∑–∫—É - —Å–±—Ä–æ—Å –∫–æ–º–±–æ
      setCombo(0);
      setComboTime(0);
    }
    
    const unfoundBugs = bugs.filter(bug => !bug.found);
    if (unfoundBugs.length > 0) {
      const randomBug = unfoundBugs[Math.floor(Math.random() * unfoundBugs.length)];
      setHint(`–ù–∞–π–¥–∏: ${randomBug.type.emoji} ${randomBug.type.name}`);
      setTimeout(() => setHint(''), 3000); // –ü–æ–¥—Å–∫–∞–∑–∫–∞ –∏—Å—á–µ–∑–∞–µ—Ç —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã
    }
  };

  // –ü–æ–ª—É—á–∏—Ç—å —Å—Ç–∏–ª—å –±–∞–≥–∞
