        }}>
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.5 }}
            style={{
              height: '100%',
              background: 'linear-gradient(90deg, #ff0000, #ff6600)',
              borderRadius: '10px'
            }}
          />
        </div>

        {/* –ë–∞–≥–∏ */}
        {bugs.map(bug => (
          <motion.div
            key={bug.id}
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            whileHover={{ scale: 1.2 }}
            style={getBugStyle(bug)}
            onClick={() => handleBugClick(bug.id)}
          >
            {bug.type.emoji}
          </motion.div>
        ))}

        {/* –ü–æ–¥—Å–∫–∞–∑–∫–∞ */}
        {hint && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              background: 'rgba(0,0,0,0.8)',
              color: '#ff6600',
              padding: '20px',
              borderRadius: '10px',
              zIndex: 20,
              textAlign: 'center'
            }}
          >
            <h3>{hint}</h3>
          </motion.div>
        )}
      </div>

      {/* –ö–Ω–æ–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={showHintForBug}
          style={{
            background: 'linear-gradient(45deg, #666, #999)',
            border: 'none',
            color: 'white',
            padding: '12px 24px',
            borderRadius: '10px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          üí° –ü–æ–¥—Å–∫–∞–∑–∫–∞ (-–ö–æ–º–±–æ)
        </motion.button>
        
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => setGameStatus('rules')}
          style={{
            background: 'linear-gradient(45deg, #0066ff, #00aaff)',
            border: 'none',
            color: 'white',
            padding: '12px 24px',
            borderRadius: '10px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          üìñ –ü—Ä–∞–≤–∏–ª–∞
        </motion.button>
      </div>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ: –ü—Ä–∞–≤–∏–ª–∞ */}
      <AnimatePresence>
        {gameStatus === 'rules' && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'rgba(0, 0, 0, 0.95)',
              zIndex: 1000,
