                <p style={{ color: '#cccccc', fontSize: '18px', marginBottom: '30px' }}>
                  –í—ã –Ω–∞—à–ª–∏ {foundBugs.length} –∏–∑ {bugs.length} –æ—à–∏–±–æ–∫
                  <br />
                  –∏ –∑–∞—Ä–∞–±–æ—Ç–∞–ª–∏ {score} –æ—á–∫–æ–≤
                </p>
              </>
            )}
            
            {gameStatus === 'completed' && (
              <>
                <h1 style={{ fontSize: '36px', color: '#ff0000', marginBottom: '20px' }}>
                  üéÆ –ò–ì–†–ê –ó–ê–í–ï–†–®–ï–ù–ê
                </h1>
                <p style={{ color: '#cccccc', fontSize: '18px', marginBottom: '30px', lineHeight: '1.6' }}>
                  –í—ã —É–∂–µ –ø–æ–ª—É—á–∏–ª–∏ –ø—Ä–æ–º–æ–∫–æ–¥ —Å —ç—Ç–æ–≥–æ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞.
                  <br />
                  <strong style={{ color: '#ff0000' }}>1 –ø—Ä–æ–º–æ–∫–æ–¥ = 1 —É—Å—Ç—Ä–æ–π—Å—Ç–≤–æ</strong>
                </p>
              </>
            )}

            {/* –ü—Ä–æ–º–æ–∫–æ–¥ (–µ—Å–ª–∏ –µ—Å—Ç—å) */}
            {promoCode && (
              <motion.div
                initial={{ scale: 0.9 }}
                animate={{ scale: 1 }}
                transition={{ delay: 0.2 }}
                style={{
                  background: 'rgba(255, 255, 0, 0.1)',
                  border: '2px solid #ffff00',
                  borderRadius: '15px',
                  padding: '20px',
                  marginBottom: '30px'
                }}
              >
                <p style={{ color: '#ffff99', marginBottom: '10px', fontSize: '16px' }}>
                  {gameStatus === 'win' ? 'üéÅ –í–ê–® –ü–†–û–ú–û–ö–û–î:' : '–í–∞—à –ø—Ä–æ–º–æ–∫–æ–¥:'}
                </p>
                <div style={{
                  fontSize: '32px',
                  color: '#ffff00',
                  fontWeight: 'bold',
                  fontFamily: 'monospace',
                  letterSpacing: '2px',
                  background: 'rgba(0, 0, 0, 0.5)',
                  padding: '15px',
                  borderRadius: '10px',
                  border: '2px dashed #ffff00',
                  margin: '15px 0'
                }}>
                  {promoCode.code}
                </div>
                <p style={{ color: '#ffff99', fontSize: '16px', marginTop: '10px' }}>
                  –°–∫–∏–¥–∫–∞ {promoCode.discount}% –Ω–∞ —É—Å–ª—É–≥–∏
                </p>
                {gameStatus === 'win' && (
                  <p style={{ color: '#ffff99', fontSize: '12px', marginTop: '10px' }}>
                    –î–µ–π—Å—Ç–≤–∏—Ç–µ–ª–µ–Ω 30 –¥–Ω–µ–π
                  </p>
                )}
              </motion.div>
            )}

            {/* –ö–Ω–æ–ø–∫–∏ –¥–µ–π—Å—Ç–≤–∏–π */}
            <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
              {gameStatus === 'win' && promoCode && (
                <>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={copyPromoCode}
                    style={{
                      background: 'linear-gradient(45deg, #ffff00, #ffaa00)',
                      border: 'none',
                      color: 'black',
                      padding: '15px 30px',
                      borderRadius: '10px',
                      fontSize: '18px',
                      fontWeight: 'bold',
                      cursor: 'pointer'
                    }}
                  >
                    üìã –°–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥
                  </motion.button>
                  
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={openTelegram}
                    style={{
                      background: 'linear-gradient(45deg, #0088cc, #00aaff)',
                      border: 'none',
                      color: 'white',
                      padding: '15px 30px',
                      borderRadius: '10px',
                      fontSize: '18px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      gap: '10px'
                    }}
                  >
                    <span>üí¨</span>
                    –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥ –≤ Telegram
                  </motion.button>
                </>
              )}

              {(gameStatus === 'completed' || gameStatus === 'win') && promoCode && (
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={openTelegram}
                  style={{
                    background: 'linear-gradient(45deg, #0088cc, #00aaff)',
                    border: 'none',
                    color: 'white',
                    padding: '15px 30px',
                    borderRadius: '10px',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '10px'
                  }}
                >
                  <span>üí¨</span>
                  –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥ –≤ Telegram
                </motion.button>
              )}

              {gameStatus === 'lose' && (
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => {
                    setGameStatus('initial');
                    resetGame();
                  }}
                  style={{
                    background: 'linear-gradient(45deg, #ff0000, #ff3333)',
                    border: 'none',
                    color: 'white',
                    padding: '15px 30px',
                    borderRadius: '10px',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    cursor: 'pointer'
                  }}
                >
                  üîÑ –ü–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å —Å–Ω–æ–≤–∞
                </motion.button>
              )}

              {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è –≤–æ–∑–≤—Ä–∞—Ç–∞ –≤ –º–µ–Ω—é */}
              <button
                onClick={() => setGameStatus('initial')}
                style={{
                  background: 'transparent',
                  border: '2px solid #666',
                  color: '#999',
                  padding: '10px 20px',
                  borderRadius: '10px',
                  fontSize: '14px',
                  cursor: 'pointer',
                  marginTop: '10px'
                }}
              >
                üè† –í—ã–π—Ç–∏ –≤ –º–µ–Ω—é
              </button>

              {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è –≤–æ–∑–≤—Ä–∞—Ç–∞ –Ω–∞ —Å–∞–π—Ç */}
              <button
                onClick={returnToMainSite}
                style={{
                  background: 'transparent',
                  border: '2px solid #666',
                  color: '#999',
                  padding: '10px 20px',
                  borderRadius: '10px',
                  fontSize: '14px',
                  cursor: 'pointer'
                }}
              >
                –í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞ —Å–∞–π—Ç
              </button>

              {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è —Ç–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏—è (–æ—á–∏—Å—Ç–∏—Ç—å localStorage) */}
              {process.env.NODE_ENV === 'development' && (
                <button
                  onClick={clearLocalStorage}
                  style={{
                    background: 'rgba(255, 0, 0, 0.1)',
                    border: '1px dashed #ff0000',
                    color: '#ff6666',
                    padding: '8px 16px',
                    borderRadius: '8px',
                    fontSize: '12px',
                    cursor: 'pointer',
                    marginTop: '20px',
                    opacity: 0.7
                  }}
                >
                  üß™ –û—á–∏—Å—Ç–∏—Ç—å –∏ –Ω–∞—á–∞—Ç—å –∑–∞–Ω–æ–≤–æ (–¥–ª—è —Ç–µ—Å—Ç–∞)
                </button>
              )}
            </div>
          </motion.div>
        )}
      </div>

      <style>
        {`
          @keyframes pulse {
            0% { opacity: 1; }
            50% { opacity: 0.5; }
            100% { opacity: 1; }
          }
        `}
      </style>
    </motion.div>
  );
};

export default GamePage;
