import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  return (
    <nav style={{
      background: 'var(--tg-bg-color, #0a0a0a)',
      padding: '15px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      borderBottom: '2px solid #ff0000',
      position: 'sticky',
      top: 0,
      zIndex: 1000
    }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
        <span style={{ color: '#ff0000', fontSize: '24px' }}>⚡</span>
        <Link to="/" style={{
          color: '#ff0000',
          textDecoration: 'none',
          fontSize: '18px',
          fontWeight: 'bold'
        }}>
          LYOKORPS TECH
        </Link>
      </div>

      <div style={{ display: 'flex', gap: '15px' }}>
        <Link
          to="/pricelist"
          style={{
            color: isActive('/pricelist') ? '#000' : '#fff',
            textDecoration: 'none',
            padding: '8px 12px',
            borderRadius: '5px',
            background: isActive('/pricelist') ? '#ff0000' : 'transparent',
            border: isActive('/pricelist') ? 'none' : '1px solid #ff0000',
            fontWeight: 'bold',
            fontSize: '14px'
          }}
        >
          Прайс-лист
        </Link>
        <Link
          to="/game"
          style={{
            color: isActive('/game') ? '#000' : '#fff',
            textDecoration: 'none',
            padding: '8px 12px',
            borderRadius: '5px',
            background: isActive('/game') ? '#ff0000' : 'transparent',
            border: isActive('/game') ? 'none' : '1px solid #ff0000',
            fontSize: '14px'
          }}
        >
          Игра
        </Link>
        <Link
          to="/contacts"
          style={{
            color: isActive('/contacts') ? '#000' : '#fff',
            textDecoration: 'none',
            padding: '8px 12px',
            borderRadius: '5px',
            background: isActive('/contacts') ? '#ff0000' : 'transparent',
            border: isActive('/contacts') ? 'none' : '1px solid #ff0000',
            fontWeight: 'bold',
            fontSize: '14px'
          }}
        >
          Контакты
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
