import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Confetti from 'react-confetti';
import { useNavigate } from 'react-router-dom'; // –î–æ–±–∞–≤–ª—è–µ–º useNavigate

const GamePage = () => {
  const navigate = useNavigate(); // –•—É–∫ –¥–ª—è –Ω–∞–≤–∏–≥–∞—Ü–∏–∏
  
  // –°–æ—Å—Ç–æ—è–Ω–∏—è –∏–≥—Ä—ã
  const [level, setLevel] = useState(1);
  const [score, setScore] = useState(0);
  const [foundBugs, setFoundBugs] = useState([]);
  const [timeLeft, setTimeLeft] = useState(60);
  const [gameStatus, setGameStatus] = useState('rules'); // rules, playing, win, lose, gameOver
  const [showConfetti, setShowConfetti] = useState(false);
  const [promoCode, setPromoCode] = useState(null);
  const [hintsUsed, setHintsUsed] = useState(0);
  const [combo, setCombo] = useState(0);
  const [lastClickTime, setLastClickTime] = useState(0);
  const [difficulty, setDifficulty] = useState('normal');
  const [specialBugs, setSpecialBugs] = useState([]);
  const [showRules, setShowRules] = useState(true);
  const [gamesPlayed, setGamesPlayed] = useState(0);
  const [gameCompleted, setGameCompleted] = useState(false);
  const [showGameCompleted, setShowGameCompleted] = useState(false);
  
  const gameAreaRef = useRef(null);
  const maxGamesPerDevice = 1;

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≤–æ–∑–≤—Ä–∞—Ç–∞ –Ω–∞ –≥–ª–∞–≤–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É
  const returnToMainSite = () => {
    navigate('/'); // –ü–µ—Ä–µ—Ö–æ–¥ –Ω–∞ –≥–ª–∞–≤–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Å—Ç—Ä–∞–Ω–∏—Ü—ã (–∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–∞)
  const refreshPage = () => {
    window.location.reload();
  };

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –∏–≥—Ä–∞–ª –ª–∏ —É–∂–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —Å —ç—Ç–æ–≥–æ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞
  useEffect(() => {
    const played = localStorage.getItem('techGamePlayed');
    const playedCount = localStorage.getItem('techGamesPlayedCount') || 0;
    const promoUsed = localStorage.getItem('techPromoUsed');
    
    if (played === 'true') {
      setGamesPlayed(parseInt(playedCount));
      if (parseInt(playedCount) >= maxGamesPerDevice) {
        setGameStatus('gameOver');
        setShowGameCompleted(true);
      }
    }
    
    if (promoUsed) {
      setPromoCode(JSON.parse(promoUsed));
    }
  }, []);

  // –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–ª–æ–∂–Ω–æ—Å—Ç–∏
  const difficultySettings = {
    easy: { bugs: 3, time: 90, bugSize: 50, pointsMultiplier: 1 },
    normal: { bugs: 5, time: 60, bugSize: 40, pointsMultiplier: 1.5 },
    hard: { bugs: 7, time: 45, bugSize: 30, pointsMultiplier: 2 },
    expert: { bugs: 10, time: 30, bugSize: 20, pointsMultiplier: 3 }
  };

  // –¢–∏–ø—ã –±–∞–≥–æ–≤
  const bugTypes = [
    { id: 1, name: '–°–ª–æ–º–∞–Ω–Ω—ã–π —á–∏–ø', emoji: 'üíæ', color: '#ff0000', points: 100, rare: false },
    { id: 2, name: '–ü–µ—Ä–µ–≥–æ—Ä–µ–≤—à–∏–π —Å–≤–µ—Ç–æ–¥–∏–æ–¥', emoji: 'üí°', color: '#ffff00', points: 150, rare: false },
    { id: 3, name: '–û—à–∏–±–∫–∞ –≤ –∫–æ–¥–µ', emoji: 'üêõ', color: '#00ff00', points: 200, rare: false },
    { id: 4, name: '–û–±—Ä—ã–≤ –ø—Ä–æ–≤–æ–¥–∞', emoji: 'üîå', color: '#ff8800', points: 120, rare: false },
    { id: 5, name: '–ü–µ—Ä–µ–≥—Ä–µ–≤ –ø—Ä–æ—Ü–µ—Å—Å–æ—Ä–∞', emoji: 'üî•', color: '#ff3300', points: 180, rare: false },
    { id: 6, name: '–í–∏—Ä—É—Å', emoji: 'ü¶†', color: '#ff00ff', points: 300, rare: true },
  ];

  // –ü—Ä–æ–º–æ–∫–æ–¥—ã (–æ–¥–∏–Ω –∑–∞ –∏–≥—Ä—É)
  const generatePromoCode = () => {
    const codes = [
      { code: 'TECH10', discount: 10 },
      { code: 'MASTER15', discount: 15 },
      { code: 'BUG20', discount: 20 },
      { code: 'LYOKORPS25', discount: 25 }
    ];
    return codes[Math.floor(Math.random() * codes.length)];
  };

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –±–∞–≥–æ–≤
  const generateBugs = () => {
    const { bugs: bugCount } = difficultySettings[difficulty];
    const bugs = [];
    const usedPositions = new Set();
    
    for (let i = 0; i < bugCount; i++) {
      let x, y;
      let attempts = 0;
      
      do {
        x = 10 + Math.random() * 80;
        y = 10 + Math.random() * 80;
        attempts++;
      } while (usedPositions.has(`${Math.floor(x)}-${Math.floor(y)}`) && attempts < 100);
      
      usedPositions.add(`${Math.floor(x)}-${Math.floor(y)}`);
      
      const isRare = Math.random() < 0.2;
      const availableTypes = bugTypes.filter(type => type.rare === isRare);
      const bugType = availableTypes[Math.floor(Math.random() * availableTypes.length)];
      
      bugs.push({
        id: Date.now() + i,
        type: bugType,
        x,
        y,
        found: false,
        blinking: Math.random() < 0.3,
        rotating: Math.random() < 0.2,
        size: difficultySettings[difficulty].bugSize * (isRare ? 1.2 : 1)
      });
    }
    
    return bugs;
  };

