                <div style={{ fontSize: '12px', color: '#88ff88' }}>–ù–ê–ô–î–ï–ù–û</div>
                <div style={{ fontSize: '24px', color: '#00ff00', fontWeight: 'bold' }}>
                  {foundBugs.length}/{bugs.length}
                </div>
              </div>
              
              <div style={{
                background: 'rgba(255, 255, 0, 0.1)',
                border: '2px solid #ffff00',
                borderRadius: '15px',
                padding: '12px 20px',
                minWidth: '110px'
              }}>
                <div style={{ fontSize: '12px', color: '#ffff88' }}>–û–ß–ö–ò</div>
                <div style={{ fontSize: '24px', color: '#ffff00', fontWeight: 'bold' }}>{score}</div>
              </div>
              
              <div style={{
                background: 'rgba(255, 0, 0, 0.1)',
                border: '2px solid #ff0000',
                borderRadius: '15px',
                padding: '12px 20px',
                minWidth: '110px'
              }}>
                <div style={{ fontSize: '12px', color: '#ff8888' }}>–í–†–ï–ú–Ø</div>
                <div style={{ 
                  fontSize: '24px', 
                  color: timeLeft > 20 ? '#ff0000' : '#ff0000',
                  fontWeight: 'bold',
                  animation: timeLeft <= 10 ? 'pulse 1s infinite' : 'none'
                }}>
                  {timeLeft}—Å
                </div>
              </div>
              
              <div style={{
                background: 'rgba(255, 0, 255, 0.1)',
                border: '2px solid #ff00ff',
                borderRadius: '15px',
                padding: '12px 20px',
                minWidth: '110px'
              }}>
                <div style={{ fontSize: '12px', color: '#ff88ff' }}>–ö–û–ú–ë–û</div>
                <div style={{ fontSize: '24px', color: '#ff00ff', fontWeight: 'bold' }}>x{combo}</div>
              </div>
            </div>
            
            {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –±–∞—Ä */}
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '10px',
              height: '10px',
              margin: '0 auto 20px',
              maxWidth: '600px',
              overflow: 'hidden'
            }}>
              <motion.div
                initial={{ width: 0 }}
                animate={{ width: `${progress}%` }}
                transition={{ duration: 0.5 }}
                style={{
                  height: '100%',
                  background: 'linear-gradient(90deg, #00ff00, #00cc00)',
                  borderRadius: '10px'
                }}
              />
            </div>
          </div>

          {/* –ò–≥—Ä–æ–≤–æ–µ –ø–æ–ª–µ */}
          <div 
            ref={gameAreaRef}
            style={{
              position: 'relative',
              background: 'rgba(0, 50, 50, 0.3)',
              border: '4px solid #00aaaa',
              borderRadius: '20px',
              height: '500px',
              margin: '0 auto 30px',
              overflow: 'hidden'
            }}
          >
            {/* –ë–∞–≥–∏ */}
            <AnimatePresence>
              {bugs.map((bug) => (
                !bug.found && (
                  <motion.div
                    key={bug.id}
                    initial={{ scale: 0, opacity: 0 }}
                    animate={{ 
                      scale: bug.blinking ? [1, 1.2, 1] : 1,
                      opacity: 1,
                      rotate: bug.rotating ? 360 : 0
                    }}
                    transition={{
                      scale: bug.blinking ? { repeat: Infinity, duration: 0.5 } : {},
                      rotate: bug.rotating ? { repeat: Infinity, duration: 2 } : {}
                    }}
                    whileHover={{ scale: 1.3 }}
                    whileTap={{ scale: 0.7 }}
                    onClick={() => handleBugClick(bug.id)}
                    style={{
                      position: 'absolute',
                      left: `${bug.x}%`,
                      top: `${bug.y}%`,
                      width: `${bug.size}px`,
                      height: `${bug.size}px`,
                      background: `radial-gradient(circle, ${bug.type.color}, ${bug.type.color}44)`,
                      borderRadius: '50%',
                      cursor: 'pointer',
                      border: `3px solid ${bug.type.color}`,
                      boxShadow: `0 0 20px ${bug.type.color}`,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontWeight: 'bold',
                      fontSize: `${bug.size * 0.4}px`,
                      zIndex: 2,
                      transform: 'translate(-50%, -50%)'
                    }}
                  >
                    {bug.type.emoji}
                  </motion.div>
                )
              ))}
            </AnimatePresence>

            {/* –ü–æ–¥—Å–∫–∞–∑–∫–∏ */}
            {specialBugs.map((special, index) => {
              const bug = bugs.find(b => b.id === special.bugId);
              if (!bug || bug.found) return null;
              
              return (
                <motion.div
                  key={index}
                  animate={{ 
                    boxShadow: ['0 0 30px #ffff00', '0 0 60px #ffff00', '0 0 30px #ffff00']
                  }}
                  transition={{ repeat: Infinity, duration: 1 }}
                  style={{
                    position: 'absolute',
                    left: `${bug.x}%`,
                    top: `${bug.y}%`,
                    width: `${bug.size * 2}px`,
                    height: `${bug.size * 2}px`,
                    borderRadius: '50%',
                    border: '3px dashed #ffff00',
                    transform: 'translate(-50%, -50%)',
                    pointerEvents: 'none'
                  }}
                />
              );
            })}
          </div>

          {/* –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '15px',
            marginBottom: '30px',
            flexWrap: 'wrap'
          }}>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={useHint}
              disabled={hintsUsed >= 3 || gameStatus !== 'playing'}
              style={{
                background: hintsUsed >= 3 
                  ? 'linear-gradient(45deg, #666, #888)' 
                  : 'linear-gradient(45deg, #ffff00, #ffaa00)',
                border: 'none',
                color: hintsUsed >= 3 ? '#aaa' : 'black',
                padding: '12px 25px',
                borderRadius: '10px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: hintsUsed >= 3 ? 'not-allowed' : 'pointer'
              }}
            >
              üí° –ü–æ–¥—Å–∫–∞–∑–∫–∞ ({3 - hintsUsed})
            </motion.button>
            
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setShowRules(true)}
              style={{
                background: 'linear-gradient(45deg, #666, #888)',
                border: 'none',
                color: 'white',
                padding: '12px 25px',
                borderRadius: '10px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              üìñ –ü—Ä–∞–≤–∏–ª–∞
            </motion.button>
          </div>

          {/* –†–µ–∑—É–ª—å—Ç–∞—Ç –ø–æ–±–µ–¥—ã */}
          <AnimatePresence>
            {gameStatus === 'win' && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                style={{
                  position: 'fixed',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  background: 'rgba(0, 0, 0, 0.95)',
                  zIndex: 1000,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  padding: '20px'
                }}
              >
                <motion.div
                  initial={{ scale: 0.8 }}
                  animate={{ scale: 1 }}
                  style={{
                    background: 'linear-gradient(135deg, #000000 0%, #1a0000 100%)',
                    border: '3px solid #00ff00',
                    borderRadius: '20px',
                    padding: '40px',
                    maxWidth: '500px',
                    width: '100%',
                    textAlign: 'center',
                    position: 'relative'
                  }}
                >
                  {/* –ö—Ä–µ—Å—Ç–∏–∫ –¥–ª—è –∑–∞–∫—Ä—ã—Ç–∏—è */}
                  <motion.button
                    whileHover={{ scale: 1.2, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={returnToMainSite} // –ò—Å–ø–æ–ª—å–∑—É–µ–º navigate –≤–º–µ—Å—Ç–æ reload
                    style={{
                      position: 'absolute',
                      top: '15px',
                      right: '15px',
                      background: 'rgba(255, 0, 0, 0.2)',
                      border: '2px solid #ff0000',
                      color: '#ff0000',
                      width: '40px',
                      height: '40px',
                      borderRadius: '50%',
                      fontSize: '20px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      zIndex: 1001
                    }}
                  >
                    √ó
                  </motion.button>

                  <h1 style={{ fontSize: '36px', color: '#00ff00', marginBottom: '20px' }}>
                    üèÜ –ü–û–ë–ï–î–ê!
                  </h1>
                  
                  <p style={{ color: '#cccccc', fontSize: '18px', marginBottom: '30px' }}>
                    –í—ã –Ω–∞—à–ª–∏ –≤—Å–µ {bugs.length} –æ—à–∏–±–æ–∫ –∏ –∑–∞—Ä–∞–±–æ—Ç–∞–ª–∏ {score} –æ—á–∫–æ–≤!
                  </p>
                  
                  <div style={{
                    background: 'rgba(255, 255, 0, 0.1)',
                    border: '2px solid #ffff00',
                    borderRadius: '15px',
                    padding: '20px',
                    marginBottom: '30px'
                  }}>
                    <h2 style={{ color: '#ffff00', marginBottom: '15px' }}>üéÅ –í–ê–® –ü–†–û–ú–û–ö–û–î:</h2>
                    <div style={{
                      fontSize: '32px',
                      color: '#ffff00',
                      fontWeight: 'bold',
                      fontFamily: 'monospace',
                      letterSpacing: '2px',
                      background: 'rgba(0, 0, 0, 0.5)',
                      padding: '15px',
                      borderRadius: '10px',
                      border: '2px dashed #ffff00',
                      marginBottom: '15px'
                    }}>
                      {promoCode?.code || 'ERROR'}
                    </div>
                    <p style={{ color: '#ffff99' }}>
                      –°–∫–∏–¥–∫–∞ {promoCode?.discount || 10}% –Ω–∞ —É—Å–ª—É–≥–∏ —Ç–µ—Ö–Ω–∏—á–µ—Å–∫–æ–≥–æ —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç–∞
                    </p>
                    <p style={{ color: '#ffff99', fontSize: '12px', marginTop: '10px' }}>
                      –î–µ–π—Å—Ç–≤–∏—Ç–µ–ª–µ–Ω 30 –¥–Ω–µ–π
                    </p>
                  </div>
                  
                  <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={copyPromoCode}
                      style={{
                        background: 'linear-gradient(45deg, #ffff00, #ffaa00)',
                        border: 'none',
                        color: 'black',
                        padding: '15px',
                        borderRadius: '10px',
                        fontSize: '18px',
                        fontWeight: 'bold',
                        cursor: 'pointer'
                      }}
                    >
                      üìã –°–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥
                    </motion.button>
                    
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={openTelegram}
                      style={{
                        background: 'linear-gradient(45deg, #0088cc, #00aaff)',
                        border: 'none',
                        color: 'white',
                        padding: '15px',
                        borderRadius: '10px',
                        fontSize: '18px',
                        fontWeight: 'bold',
                        cursor: 'pointer'
                      }}
                    >
                      üí¨ –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –≤ Telegram
                    </motion.button>
                    
                    {/* –ò—Å–ø–æ–ª—å–∑—É–µ–º navigate –≤–º–µ—Å—Ç–æ reload */}
                    <button
                      onClick={returnToMainSite}
                      style={{
                        background: 'transparent',
                        border: '2px solid #666',
                        color: '#999',
                        padding: '12px',
                        borderRadius: '10px',
                        fontSize: '14px',
                        cursor: 'pointer'
                      }}
                    >
                      –í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞ —Å–∞–π—Ç
                    </button>
                  </div>
                </motion.div>
              </motion.div>
            )}
          </AnimatePresence>

          {/* –†–µ–∑—É–ª—å—Ç–∞—Ç –ø—Ä–æ–∏–≥—Ä—ã—à–∞ */}
          <AnimatePresence>
            {gameStatus === 'lose' && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                style={{
                  position: 'fixed',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  background: 'rgba(0, 0, 0, 0.95)',
                  zIndex: 1000,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  padding: '20px'
                }}
              >
                <motion.div
                  initial={{ scale: 0.8 }}
                  animate={{ scale: 1 }}
                  style={{
                    background: 'linear-gradient(135deg, #000000 0%, #1a0000 100%)',
                    border: '3px solid #ff0000',
                    borderRadius: '20px',
                    padding: '40px',
                    maxWidth: '500px',
                    width: '100%',
                    textAlign: 'center',
                    position: 'relative'
                  }}
                >
                  {/* –ö—Ä–µ—Å—Ç–∏–∫ –¥–ª—è –∑–∞–∫—Ä—ã—Ç–∏—è */}
                  <motion.button
                    whileHover={{ scale: 1.2, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={() => {
                      setGameStatus('playing');
                      resetGame();
                    }}
                    style={{
                      position: 'absolute',
                      top: '15px',
                      right: '15px',
                      background: 'rgba(255, 0, 0, 0.2)',
                      border: '2px solid #ff0000',
                      color: '#ff0000',
                      width: '40px',
                      height: '40px',
                      borderRadius: '50%',
                      fontSize: '20px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      zIndex: 1001
                    }}
                  >
                    √ó
                  </motion.button>

                  <h1 style={{ fontSize: '36px', color: '#ff0000', marginBottom: '20px' }}>
                    üíÄ –í–†–ï–ú–Ø –í–´–®–õ–û!
                  </h1>
                  
                  <p style={{ color: '#cccccc', fontSize: '18px', marginBottom: '30px' }}>
                    –í—ã –Ω–∞—à–ª–∏ {foundBugs.length} –∏–∑ {bugs.length} –æ—à–∏–±–æ–∫
                    <br />
                    –∏ –∑–∞—Ä–∞–±–æ—Ç–∞–ª–∏ {score} –æ—á–∫–æ–≤
                  </p>
                  
                  <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={() => {
                        setGameStatus('playing');
                        resetGame();
                      }}
                      style={{
                        background: 'linear-gradient(45deg, #ff0000, #ff3333)',
                        border: 'none',
                        color: 'white',
                        padding: '15px',
                        borderRadius: '10px',
                        fontSize: '18px',
                        fontWeight: 'bold',
                        cursor: 'pointer'
                      }}
                    >
                      üîÑ –ü–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å —Å–Ω–æ–≤–∞
                    </motion.button>
                    
                    {/* –ò—Å–ø–æ–ª—å–∑—É–µ–º navigate –≤–º–µ—Å—Ç–æ reload */}
                    <button
                      onClick={returnToMainSite}
                      style={{
                        background: 'transparent',
                        border: '2px solid #666',
                        color: '#999',
                        padding: '12px',
                        borderRadius: '10px',
                        fontSize: '14px',
                        cursor: 'pointer'
                      }}
                    >
                      –í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞ —Å–∞–π—Ç
                    </button>
                  </div>
                </motion.div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      )}

      <style>
        {`
          @keyframes pulse {
            0% { opacity: 1; }
            50% { opacity: 0.5; }
            100% { opacity: 1; }
          }
        `}
      </style>
    </motion.div>
  );
};

export default GamePage;
