import React, { useState } from 'react';

const HomePage = () => {
  const services = [
    {
      emoji: 'ü§ñ',
      title: '–ß–∞—Ç-–±–æ—Ç—ã',
      description: '–ê–≤—Ç–æ–º–∞—Ç–∏–∑–∏—Ä—É—é –æ–±—â–µ–Ω–∏–µ —Å –∫–ª–∏–µ–Ω—Ç–∞–º–∏, —Å–±–æ—Ä –∑–∞—è–≤–æ–∫ –∏ –∫–æ–Ω—Å—É–ª—å—Ç–∞—Ü–∏–∏ —á–µ—Ä–µ–∑ Telegram, WhatsApp –∏ –¥—Ä—É–≥–∏–µ –º–µ—Å—Å–µ–Ω–¥–∂–µ—Ä—ã.',
      value: '–î–ª—è –≤–∞—Å: —ç–∫–æ–Ω–æ–º–∏—è –≤—Ä–µ–º–µ–Ω–∏ –Ω–∞ —Ä—É—Ç–∏–Ω–Ω—ã—Ö –æ—Ç–≤–µ—Ç–∞—Ö, –∫—Ä—É–≥–ª–æ—Å—É—Ç–æ—á–Ω–∞—è —Ä–∞–±–æ—Ç–∞, —É–≤–µ–ª–∏—á–µ–Ω–∏–µ –∫–æ–Ω–≤–µ—Ä—Å–∏–∏ –∑–∞—è–≤–æ–∫.'
    },
    {
      emoji: 'üé•',
      title: '–ê–≤—Ç–æ–≤–µ–±–∏–Ω–∞—Ä—ã',
      description: '–°–æ–∑–¥–∞—é –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–µ –≤–µ–±–∏–Ω–∞—Ä—ã, –∫–æ—Ç–æ—Ä—ã–µ –ø—Ä–æ–¥–∞—é—Ç 24/7 –±–µ–∑ –≤–∞—à–µ–≥–æ —É—á–∞—Å—Ç–∏—è.',
      value: '–î–ª—è –≤–∞—Å: –ø–∞—Å—Å–∏–≤–Ω—ã–π –ø–æ—Ç–æ–∫ –∫–ª–∏–µ–Ω—Ç–æ–≤, –º–∞—Å—à—Ç–∞–±–∏—Ä–æ–≤–∞–Ω–∏–µ —ç–∫—Å–ø–µ—Ä—Ç–∏–∑—ã, —Å–Ω–∏–∂–µ–Ω–∏–µ –∑–∞—Ç—Ä–∞—Ç –Ω–∞ –ø—Ä–æ–≤–µ–¥–µ–Ω–∏–µ –∂–∏–≤—ã—Ö –≤–µ–±–∏–Ω–∞—Ä–æ–≤.'
    },
    {
      emoji: 'üöÄ',
      title: '–ó–∞–ø—É—Å–∫–∏',
      description: '–ö–æ–º–ø–ª–µ–∫—Å–Ω–∞—è —Ç–µ—Ö–Ω–∏—á–µ—Å–∫–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è –∑–∞–ø—É—Å–∫–æ–≤ –æ–Ω–ª–∞–π–Ω-–∫—É—Ä—Å–æ–≤, –º–∞—Ä–∞—Ñ–æ–Ω–æ–≤ –∏ –ø—Ä–æ–≥—Ä–∞–º–º.',
      value: '–î–ª—è –≤–∞—Å: –Ω–∞–¥–µ–∂–Ω–∞—è —Ç–µ—Ö–ø–æ–¥–¥–µ—Ä–∂–∫–∞ –∑–∞–ø—É—Å–∫–∞, –º–∏–Ω–∏–º–∏–∑–∞—Ü–∏—è —Ç–µ—Ö–Ω–∏—á–µ—Å–∫–∏—Ö —Å–±–æ–µ–≤, –∞–≤—Ç–æ–º–∞—Ç–∏–∑–∞—Ü–∏—è –ø—Ä–æ—Ü–µ—Å—Å–æ–≤ –ø—Ä–æ–¥–∞–∂.'
    },
    {
      emoji: 'üíª',
      title: '–¢–µ—Ö–ø–æ–¥–¥–µ—Ä–∂–∫–∞',
      description: '–ü–æ—Å—Ç–æ—è–Ω–Ω–æ–µ —Å–æ–ø—Ä–æ–≤–æ–∂–¥–µ–Ω–∏–µ –∏ —Ä–∞–∑–≤–∏—Ç–∏–µ –≤–∞—à–∏—Ö digital-–ø—Ä–æ–¥—É–∫—Ç–æ–≤.',
      value: '–î–ª—è –≤–∞—Å: —Å—Ç–∞–±–∏–ª—å–Ω–∞—è —Ä–∞–±–æ—Ç–∞ —Å–∏—Å—Ç–µ–º, –æ–ø–µ—Ä–∞—Ç–∏–≤–Ω–æ–µ —Ä–µ—à–µ–Ω–∏–µ –ø—Ä–æ–±–ª–µ–º, –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å —Ñ–æ–∫—É—Å–∏—Ä–æ–≤–∞—Ç—å—Å—è –Ω–∞ –±–∏–∑–Ω–µ—Å–µ.'
    }
  ];

  const [activeService, setActiveService] = useState(null);

  const handleServiceClick = (index) => {
    // –ï—Å–ª–∏ –Ω–∞–∂–∏–º–∞–µ–º –Ω–∞ —É–∂–µ –∞–∫—Ç–∏–≤–Ω—É—é —É—Å–ª—É–≥—É - —Å–∫—Ä—ã–≤–∞–µ–º
    // –ï—Å–ª–∏ –Ω–∞ –¥—Ä—É–≥—É—é - –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –Ω–æ–≤—É—é
    setActiveService(activeService === index ? null : index);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(45deg, #380303 0%, #1a0000 100%)',
      padding: '20px 15px',
      textAlign: 'center',
      color: 'white',
      fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif'
    }}>
      <h1 style={{ 
        fontSize: '32px', 
        color: '#f30808',
        marginBottom: '15px',
        textShadow: '0 2px 4px rgba(0,0,0,0.5)'
      }}>
        üî• –¢–ï–•–ù–ò–ß–ï–°–ö–ò–ô –°–ü–ï–¶–ò–ê–õ–ò–°–¢
      </h1>
      
      <p style={{ 
        fontSize: '16px', 
        marginBottom: '30px',
        lineHeight: '1.5',
        opacity: '0.9'
      }}>
        –¢–µ—Ö-–ª–∏–¥ / Digital-—Ç–µ—Ö–Ω–æ–ª–æ–≥<br/>
        –ê–≤—Ç–æ–≤–µ–±–∏–Ω–∞—Ä—ã ‚Ä¢ –ß–∞—Ç-–±–æ—Ç—ã ‚Ä¢ –ö–æ–º–ø–ª–µ–∫—Å–Ω—ã–µ –∑–∞–ø—É—Å–∫–∏
      </p>

      {/* –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä –¥–ª—è —É—Å–ª—É–≥ */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(2, 1fr)',
        gap: '12px',
        maxWidth: '400px',
        margin: '0 auto'
      }}>
        {services.map((service, index) => (
          <div
            key={index}
            style={{
              background: 'rgba(236, 14, 14, 0.1)',
              border: '1px solid rgba(255, 0, 0, 0.3)',
              borderRadius: '12px',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              overflow: 'hidden', // –ß—Ç–æ–±—ã —Å–∫—Ä—ã—Ç—å –≤—ã—Ö–æ–¥—è—â–∏–π –∫–æ–Ω—Ç–µ–Ω—Ç
              position: 'relative'
            }}
            onClick={() => handleServiceClick(index)}
          >
            {/* –í–µ—Ä—Ö–Ω—è—è —á–∞—Å—Ç—å —Å –∏–∫–æ–Ω–∫–æ–π –∏ –∑–∞–≥–æ–ª–æ–≤–∫–æ–º */}
            <div style={{
              padding: '20px 15px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              minHeight: '120px'
            }}>
              <div style={{ fontSize: '36px', marginBottom: '10px' }}>
                {service.emoji}
              </div>
              <div style={{ 
                fontSize: '16px', 
                fontWeight: 'bold',
                color: activeService === index ? '#f30808' : 'white',
                transition: 'color 0.3s ease'
              }}>
                {service.title}
              </div>
            </div>

            {/* –í—ã–ø–∞–¥–∞—é—â–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è */}
            <div style={{
              height: activeService === index ? 'auto' : '0',
              opacity: activeService === index ? '1' : '0',
              overflow: 'hidden',
              transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
              background: 'rgba(0, 0, 0, 0.4)',
